// MovieDetails.jsx
import React, { useState, useEffect } from "react";
import axios from "axios";
import { useParams } from "react-router-dom";
import {
  Box,
  Typography,
  Container,
  Grid,
  Card,
  CardContent,
  CardMedia,
  Chip,
  Rating,
  Button,
  Dialog,
  DialogContent,
  IconButton,
  Avatar,
  Divider,
  Paper,
  styled,
  Snackbar,
  Alert,
} from "@mui/material";
import {
  PlayArrow,
  Close,
  Star,
  Schedule,
  CalendarToday,
  Language,
  ThumbUp,
  ThumbDown,
  BookmarkAdd,
  BookmarkAdded,
  BookmarkRemove,
  EmojiEvents
} from "@mui/icons-material";
import url from "../constants/url";

const StyledContainer = styled(Container)`
  padding: 0;
  max-width: none !important;
`;

const HeroSection = styled(Box)`
  position: relative;
  min-height: 70vh;
  background-size: cover;
  background-position: center;
  display: flex;
  align-items: center;
  color: white;
`;

const HeroOverlay = styled(Box)`
  position: absolute;
  top: 0;
  left: 0;
  right: 0;
  bottom: 0;
  background: linear-gradient(
    45deg,
    rgba(0, 0, 0, 0.9) 0%,
    rgba(0, 0, 0, 0.7) 50%,
    rgba(0, 0, 0, 0.4) 100%
  );
`;

const ContentSection = styled(Container)`
  position: relative;
  z-index: 2;
  padding: 40px 20px;
`;

const TitleImage = styled('img')`
  max-height: 200px;
  width: auto;
  object-fit: contain;
  filter: drop-shadow(0 4px 20px rgba(255, 255, 255, 0.3));
  margin-bottom: 20px;
`;

const InfoCard = styled(Card)`
  background: rgba(30, 30, 30, 0.95);
  color: white;
  margin-bottom: 30px;
  backdrop-filter: blur(10px);
`;

const TrailerButton = styled(Button)`
  background: linear-gradient(45deg, #ff4081, #ff6ec7);
  color: white;
  padding: 12px 30px;
  border-radius: 25px;
  font-weight: bold;
  text-transform: none;
  margin: 10px;
  &:hover {
    background: linear-gradient(45deg, #e91e63, #ff4081);
    transform: translateY(-2px);
    box-shadow: 0 8px 25px rgba(255, 64, 129, 0.3);
  }
`;

const WatchlistButton = styled(Button)`
  background: linear-gradient(45deg, #4fc3f7, #29b6f6);
  color: white;
  padding: 12px 30px;
  border-radius: 25px;
  font-weight: bold;
  text-transform: none;
  margin: 10px;
  transition: all 0.3s ease;
  &:hover {
    background: linear-gradient(45deg, #29b6f6, #0288d1);
    transform: translateY(-2px);
    box-shadow: 0 8px 25px rgba(79, 195, 247, 0.3);
  }
  &.added {
    background: linear-gradient(45deg, #66bb6a, #4caf50);
    &:hover {
      background: linear-gradient(45deg, #4caf50, #388e3c);
      box-shadow: 0 8px 25px rgba(76, 175, 80, 0.3);
    }
  }
  &.remove {
    background: linear-gradient(45deg, #f44336, #d32f2f);
    &:hover {
      background: linear-gradient(45deg, #d32f2f, #c62828);
      box-shadow: 0 8px 25px rgba(244, 67, 54, 0.3);
    }
  }
`;

const GenreChip = styled(Chip)`
  background: linear-gradient(45deg, #7b1fa2, #9c27b0);
  color: white;
  margin: 4px;
  font-weight: 500;
  &:hover {
    background: linear-gradient(45deg, #6a1b9a, #8e24aa);
  }
`;

const CastCard = styled(Card)`
  background: rgba(40, 40, 40, 0.9);
  color: white;
  height: 100%;
  transition: transform 0.3s ease;
  &:hover {
    transform: translateY(-5px);
  }
`;

const ReviewCard = styled(Paper)`
  background: rgba(50, 50, 50, 0.9);
  color: white;
  padding: 20px;
  margin-bottom: 15px;
  border-radius: 12px;
`;

const AwardCard = styled(Card)`
  background: linear-gradient(135deg, rgba(255, 215, 0, 0.1) 0%, rgba(255, 193, 7, 0.05) 100%);
  border: 1px solid rgba(255, 215, 0, 0.3);
  color: white;
  height: 100%;
  transition: all 0.3s ease;
  &:hover {
    transform: translateY(-5px);
    box-shadow: 0 8px 25px rgba(255, 215, 0, 0.2);
    border-color: rgba(255, 215, 0, 0.5);
  }
`;

function MovieDetails() {
  const { id } = useParams();
  const [movie, setMovie] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [trailerOpen, setTrailerOpen] = useState(false);
  const [selectedTrailer, setSelectedTrailer] = useState(null);
  const [isInWatchlist, setIsInWatchlist] = useState(false);
  const [snackbarOpen, setSnackbarOpen] = useState(false);
  const [snackbarMessage, setSnackbarMessage] = useState('');
  const [watchlistLoading, setWatchlistLoading] = useState(false);
  const [watchlistChecked, setWatchlistChecked] = useState(false);
  const [isAuthenticated, setIsAuthenticated] = useState(false);

  // Check authentication status
  const checkAuthStatus = () => {
    const token = localStorage.getItem('token');
    setIsAuthenticated(!!token);
    return !!token;
  };

  // Fetch movie details
  const fetchMovieDetails = async (movieId) => {
    try {
      const response = await axios.get(`${url}/api/movies/${movieId}`);
      setMovie(response.data);
      setError(null);
    } catch (error) {
      console.error("Error fetching movie details:", error);
      setError("Movie not found");
      throw error;
    }
  };

  // Check if movie is in watchlist
  const checkWatchlistStatus = async (movieId) => {
    try {
      const token = localStorage.getItem('token');
      if (!token) {
        setIsInWatchlist(false);
        setWatchlistChecked(true);
        return;
      }

      setWatchlistLoading(true);
      const response = await axios.get(`${url}/api/watchlist/check/${movieId}`, {
        headers: {
          'Authorization': `Bearer ${token}`
        }
      });
      
      setIsInWatchlist(response.data.inWatchlist);
      setWatchlistChecked(true);
    } catch (error) {
      console.error("Error checking watchlist status:", error);
      // If token is invalid, clear it
      if (error.response?.status === 401) {
        localStorage.removeItem('token');
        localStorage.removeItem('user');
        setIsAuthenticated(false);
      }
      setIsInWatchlist(false);
      setWatchlistChecked(true);
    } finally {
      setWatchlistLoading(false);
    }
  };

  // Initialize component
  useEffect(() => {
    const initializeComponent = async () => {
      if (!id) return;
      
      setLoading(true);
      setWatchlistChecked(false);
      
      // Check authentication status
      const authenticated = checkAuthStatus();
      
      try {
        // Fetch movie details
        await fetchMovieDetails(id);
        
        // Check watchlist status if authenticated
        if (authenticated) {
          await checkWatchlistStatus(id);
        } else {
          setWatchlistChecked(true);
          setIsInWatchlist(false);
        }
      } catch (error) {
        console.error("Error initializing component:", error);
      } finally {
        setLoading(false);
      }
    };

    initializeComponent();
  }, [id]);

  // Listen for authentication changes (including login/logout in other tabs)
  useEffect(() => {
    const handleAuthChange = () => {
      const wasAuthenticated = isAuthenticated;
      const nowAuthenticated = checkAuthStatus();
      
      if (nowAuthenticated !== wasAuthenticated) {
        if (nowAuthenticated && id) {
          // User just logged in, check watchlist status
          checkWatchlistStatus(id);
        } else if (!nowAuthenticated) {
          // User just logged out, reset watchlist status
          setIsInWatchlist(false);
          setWatchlistChecked(true);
        }
      }
    };

    // Listen for storage changes (login/logout in another tab)
    window.addEventListener('storage', handleAuthChange);
    
    // Also listen for focus events to catch auth changes
    window.addEventListener('focus', handleAuthChange);
    
    return () => {
      window.removeEventListener('storage', handleAuthChange);
      window.removeEventListener('focus', handleAuthChange);
    };
  }, [id, isAuthenticated]);

  // Handle watchlist toggle
  const handleWatchlistToggle = async () => {
    const token = localStorage.getItem('token');
    if (!token) {
      setSnackbarMessage('Please log in to manage your watchlist');
      setSnackbarOpen(true);
      return;
    }

    setWatchlistLoading(true);

    try {
      if (isInWatchlist) {
        // Remove from watchlist
        await axios.delete(`${url}/api/watchlist/remove`, {
          data: { movieid: parseInt(id) },
          headers: {
            'Authorization': `Bearer ${token}`
          }
        });
        
        setIsInWatchlist(false);
        setSnackbarMessage('Movie removed from watchlist');
      } else {
        // Add to watchlist
        await axios.post(`${url}/api/watchlist/add`, {
          movieid: parseInt(id)
        }, {
          headers: {
            'Authorization': `Bearer ${token}`
          }
        });
        
        setIsInWatchlist(true);
        setSnackbarMessage('Movie added to watchlist');
      }
      
      setSnackbarOpen(true);
    } catch (error) {
      console.error("Error updating watchlist:", error);
      
      let errorMessage = 'Failed to update watchlist';
      if (error.response?.status === 400) {
        errorMessage = 'Movie is already in watchlist';
      } else if (error.response?.status === 401) {
        errorMessage = 'Please log in to manage your watchlist';
        // Clear token if unauthorized
        localStorage.removeItem('token');
        localStorage.removeItem('user');
        setIsInWatchlist(false);
        setIsAuthenticated(false);
      } else if (error.response?.status === 404) {
        errorMessage = isInWatchlist ? 'Movie not found in watchlist' : 'Watchlist not found';
      }
      
      setSnackbarMessage(errorMessage);
      setSnackbarOpen(true);
    } finally {
      setWatchlistLoading(false);
    }
    
  };

  const handleTrailerPlay = (trailer) => {
    setSelectedTrailer(trailer);
    setTrailerOpen(true);
  };

  const formatDate = (dateString) => {
    return new Date(dateString).toLocaleDateString('en-US', {
      year: 'numeric',
      month: 'long',
      day: 'numeric'
    });
  };

  const formatDuration = (minutes) => {
    const hours = Math.floor(minutes / 60);
    const mins = minutes % 60;
    return hours > 0 ? `${hours}h ${mins}m` : `${mins}m`;
  };

  const getAwardCategoryColor = (category) => {
    const categoryColors = {
      'Best Picture': '#ffd700',
      'Best Director': '#ff6b35',
      'Best Actor': '#4ecdc4',
      'Best Actress': '#e056fd',
      'Best Supporting Actor': '#45b7d1',
      'Best Supporting Actress': '#f9ca24',
      'Best Screenplay': '#6c5ce7',
      'Best Cinematography': '#a29bfe',
      'Best Original Score': '#fd79a8',
      'Best Visual Effects': '#00cec9',
      default: '#ddd'
    };
    return categoryColors[category] || categoryColors.default;
  };

  // Get watchlist button props - Enhanced with better status detection
  const getWatchlistButtonProps = () => {
    // If not authenticated, show login prompt
    if (!isAuthenticated) {
      return {
        startIcon: <BookmarkAdd />,
        className: '',
        text: 'Login to Add to Watchlist',
        disabled: false
      };
    }

    // If watchlist status is being checked or updated
    if (watchlistLoading || !watchlistChecked) {
      return {
        startIcon: <BookmarkAdd />,
        className: '',
        text: 'Loading...',
        disabled: true
      };
    }

    // If movie is in watchlist
    if (isInWatchlist) {
      return {
        startIcon: <BookmarkAdded />,
        className: 'added',
        text: 'In Watchlist âœ“',
        disabled: false
      };
    }

    // Default state - not in watchlist
    return {
      startIcon: <BookmarkAdd />,
      className: '',
      text: 'Add to Watchlist',
      disabled: false
    };
  };

  if (loading) {
    return (
      <Box display="flex" justifyContent="center" alignItems="center" minHeight="100vh">
        <Typography variant="h5" color="white">Loading...</Typography>
      </Box>
    );
  }

  if (error) {
    return (
      <Box display="flex" justifyContent="center" alignItems="center" minHeight="100vh">
        <Typography variant="h5" color="error">{error}</Typography>
      </Box>
    );
  }

  if (!movie) {
    return (
      <Box display="flex" justifyContent="center" alignItems="center" minHeight="100vh">
        <Typography variant="h5" color="white">Movie not found</Typography>
      </Box>
    );
  }

  const watchlistButtonProps = getWatchlistButtonProps();

  return (
    <StyledContainer>
      {/* Hero Section */}
      <HeroSection
        sx={{
          backgroundImage: `url(/img/${movie.posterimage})`,
        }}
      >
        <HeroOverlay />
        <ContentSection maxWidth="xl">
          <Grid container spacing={4} alignItems="center">
            <Grid item xs={12} md={6}>
              <Box textAlign="center">
                <TitleImage
                  src={`/img/${movie.titleimage}`}
                  alt={`${movie.title} Title`}
                />
                
                <Box display="flex" justifyContent="center" alignItems="center" gap={2} mb={2}>
                  <Rating
                    value={movie.averagerating / 2}
                    precision={0.1}
                    readOnly
                    size="large"
                  />
                  <Typography variant="h5" sx={{ color: '#f5c518', fontWeight: 'bold' }}>
                    {movie.averagerating}/10
                  </Typography>
                </Box>

                <Box display="flex" justifyContent="center" gap={2} mb={3} flexWrap="wrap">
                  <Chip
                    icon={<Language />}
                    label={movie.language}
                    variant="outlined"
                    sx={{ color: 'white', borderColor: 'white' }}
                  />
                  <Chip
                    icon={<Schedule />}
                    label={formatDuration(movie.duration)}
                    variant="outlined"
                    sx={{ color: 'white', borderColor: 'white' }}
                  />
                  <Chip
                    icon={<CalendarToday />}
                    label={formatDate(movie.releasedate)}
                    variant="outlined"
                    sx={{ color: 'white', borderColor: 'white' }}
                  />
                </Box>

                {/* Genres Section */}
                {movie.genres && movie.genres.length > 0 && (
                  <Box display="flex" justifyContent="center" flexWrap="wrap" mb={3}>
                    {movie.genres.map((genre, index) => (
                      <GenreChip
                        key={index}
                        label={genre}
                        size="medium"
                      />
                    ))}
                  </Box>
                )}

                {/* Action Buttons */}
                <Box display="flex" justifyContent="center" flexWrap="wrap">
                  {movie.trailers && movie.trailers.length > 0 && (
                    <TrailerButton
                      startIcon={<PlayArrow />}
                      onClick={() => handleTrailerPlay(movie.trailers[0])}
                      size="large"
                    >
                      Watch Trailer
                    </TrailerButton>
                  )}
                  
                  <WatchlistButton
                    startIcon={watchlistButtonProps.startIcon}
                    onClick={handleWatchlistToggle}
                    disabled={watchlistButtonProps.disabled}
                    size="large"
                    className={watchlistButtonProps.className}
                  >
                    {watchlistButtonProps.text}
                  </WatchlistButton>
                </Box>
              </Box>
            </Grid>

            <Grid item xs={12} md={6}>
            </Grid>
          </Grid>
        </ContentSection>
      </HeroSection>

      {/* Synopsis Section */}
      <Box sx={{ backgroundColor: '#121212', py: 4 }}>
        <Container maxWidth="xl">
          <Typography variant="h6" gutterBottom sx={{ color: '#f5c518' }}>
            Synopsis
          </Typography>
          <Typography variant="body1" sx={{ lineHeight: 1.7, color: 'white' }}>
            {movie.synopsis}
          </Typography>
        </Container>
      </Box>

      {/* Details Section */}
      <Box sx={{ backgroundColor: '#121212', minHeight: '100vh', py: 4 }}>
        <Container maxWidth="xl">
          {/* Awards Section */}
          {movie.awards && movie.awards.length > 0 && (
            <Box mb={5}>
              <Typography variant="h4" gutterBottom sx={{ color: 'white', mb: 3 }}>
                <EmojiEvents sx={{ mr: 2, color: '#ffd700', verticalAlign: 'middle' }} />
                Awards & Recognition
              </Typography>
              <Grid container spacing={3}>
                {movie.awards.map((award, index) => (
                  <Grid item xs={12} sm={6} md={4} key={index}>
                    <AwardCard>
                      <CardContent>
                        <Box display="flex" alignItems="flex-start" gap={2} mb={2}>
                          <EmojiEvents 
                            sx={{ 
                              color: getAwardCategoryColor(award.Category),
                              fontSize: 30,
                              mt: 0.5
                            }} 
                          />
                          <Box flex={1}>
                            <Typography variant="h6" gutterBottom sx={{ color: '#ffd700' }}>
                              {award.Name}
                            </Typography>
                            <Chip
                              label={award.Category}
                              size="small"
                              sx={{
                                backgroundColor: getAwardCategoryColor(award.Category),
                                color: 'black',
                                fontWeight: 'bold',
                                mb: 1
                              }}
                            />
                          </Box>
                        </Box>
                        
                        <Box mb={2}>
                          <Typography variant="body2" color="grey.300" gutterBottom>
                            <strong>Year:</strong> {award.Year}
                          </Typography>
                          {award.RecipientName && (
                            <Typography variant="body2" color="grey.300">
                              <strong>Recipient:</strong> {award.RecipientName}
                            </Typography>
                          )}
                          <Typography variant="body2" color="grey.400" sx={{ mt: 1 }}>
                            <strong>Type:</strong> {award.RecipientType.charAt(0).toUpperCase() + award.RecipientType.slice(1)} Award
                          </Typography>
                        </Box>
                      </CardContent>
                    </AwardCard>
                  </Grid>
                ))}
              </Grid>
            </Box>
          )}

          {/* Cast Section */}
          {movie.cast && movie.cast.length > 0 && (
            <Box mb={5}>
              <Typography variant="h4" gutterBottom sx={{ color: 'white', mb: 3 }}>
                Cast
              </Typography>
              <Grid container spacing={3}>
                {movie.cast.map((actor, index) => (
                  <Grid item xs={12} sm={6} md={4} lg={3} key={index}>
                    <CastCard>
                      <CardMedia
                        component="img"
                        height="300"
                        image={`/img/${actor.Image}`}
                        alt={actor.Name}
                        sx={{ objectFit: 'cover' }}
                      />
                      <CardContent>
                        <Typography variant="h6" gutterBottom>
                          {actor.Name}
                        </Typography>
                        <Typography variant="body2" color="grey.400" gutterBottom>
                          as {actor.RoleName}
                        </Typography>
                        <Box display="flex" alignItems="center" gap={1}>
                          <Star sx={{ color: '#f5c518', fontSize: 16 }} />
                          <Typography variant="body2">
                            {actor.CastRating}/10
                          </Typography>
                        </Box>
                        <Typography variant="body2" color="grey.500" mt={1}>
                          {actor.Nationality} â€¢ Born {formatDate(actor.DateOfBirth)}
                        </Typography>
                      </CardContent>
                    </CastCard>
                  </Grid>
                ))}
              </Grid>
            </Box>
          )}

          {/* Crew Section */}
          <Grid container spacing={4} mb={5}>
            {/* Directors */}
            {movie.directors && movie.directors.length > 0 && (
              <Grid item xs={12} md={6}>
                <Typography variant="h5" gutterBottom sx={{ color: 'white' }}>
                  Directors
                </Typography>
                {movie.directors.map((director, index) => (
                  <Card key={index} sx={{ backgroundColor: 'rgba(40, 40, 40, 0.9)', color: 'white', mb: 2 }}>
                    <CardContent>
                      <Box display="flex" alignItems="center" gap={2}>
                        <Avatar
                          src={`/img/${director.image}`}
                          alt={director.name}
                          sx={{ width: 60, height: 60 }}
                        />
                        <Box>
                          <Typography variant="h6">{director.name}</Typography>
                          <Typography variant="body2" color="grey.400">
                            {director.nationality} â€¢ Born {formatDate(director.dateofbirth)}
                          </Typography>
                          <Typography variant="body2" mt={1}>
                            {director.bio}
                          </Typography>
                        </Box>
                      </Box>
                    </CardContent>
                  </Card>
                ))}
              </Grid>
            )}

            {/* Writers */}
            {movie.writers && movie.writers.length > 0 && (
              <Grid item xs={12} md={6}>
                <Typography variant="h5" gutterBottom sx={{ color: 'white' }}>
                  Writers
                </Typography>
                {movie.writers.map((writer, index) => (
                  <Card key={index} sx={{ backgroundColor: 'rgba(40, 40, 40, 0.9)', color: 'white', mb: 2 }}>
                    <CardContent>
                      <Box display="flex" alignItems="center" gap={2}>
                        <Avatar
                          src={`/img/${writer.image}`}
                          alt={writer.name}
                          sx={{ width: 60, height: 60 }}
                        />
                        <Box>
                          <Typography variant="h6">{writer.name}</Typography>
                          <Typography variant="body2" color="grey.400">
                            {writer.nationality} â€¢ Born {formatDate(writer.dateofbirth)}
                          </Typography>
                          <Typography variant="body2" mt={1}>
                            {writer.bio}
                          </Typography>
                        </Box>
                      </Box>
                    </CardContent>
                  </Card>
                ))}
              </Grid>
            )}
          </Grid>

          {/* Studios */}
          {movie.studios && movie.studios.length > 0 && (
            <Box mb={5}>
              <Typography variant="h5" gutterBottom sx={{ color: 'white' }}>
                Production Studios
              </Typography>
              <Grid container spacing={3}>
                {movie.studios.map((studio, index) => (
                  <Grid item xs={12} sm={6} md={4} key={index}>
                    <Card sx={{ backgroundColor: 'rgba(40, 40, 40, 0.9)', color: 'white' }}>
                      <CardContent>
                        <Box display="flex" alignItems="center" gap={2} mb={2}>
                          <Avatar
                            src={`/img/${studio.logo}`}
                            alt={studio.name}
                            sx={{ width: 50, height: 50 }}
                          />
                          <Box>
                            <Typography variant="h6">{studio.name}</Typography>
                            <Typography variant="body2" color="grey.400">
                              Founded {studio.foundedyear}
                            </Typography>
                          </Box>
                        </Box>
                        {studio.website && (
                          <Button
                            variant="outlined"
                            size="small"
                            href={studio.website}
                            target="_blank"
                            sx={{ color: 'white', borderColor: 'white' }}
                          >
                            Visit Website
                          </Button>
                        )}
                      </CardContent>
                    </Card>
                  </Grid>
                ))}
              </Grid>
            </Box>
          )}

          {/* Reviews Section */}
          {movie.reviews && movie.reviews.length > 0 && (
            <Box>
              <Typography variant="h5" gutterBottom sx={{ color: 'white' }}>
                Reviews ({movie.reviews.length})
              </Typography>
              {movie.reviews.map((review, index) => (
                <ReviewCard key={index}>
                  <Box display="flex" justifyContent="space-between" alignItems="flex-start" mb={2}>
                    <Box>
                      <Typography variant="h6" gutterBottom>
                        {review.title}
                      </Typography>
                      <Box display="flex" alignItems="center" gap={1}>
                        <Rating value={review.ratingscore / 2} size="small" readOnly />
                        <Typography variant="body2">
                          {review.ratingscore}/10
                        </Typography>
                      </Box>
                    </Box>
                    {review.hasspoiler && (
                      <Chip
                        label="Spoiler"
                        color="warning"
                        size="small"
                      />
                    )}
                  </Box>
                  
                  <Typography variant="body1" mb={2} sx={{ lineHeight: 1.6 }}>
                    {review.content}
                  </Typography>
                  
                  <Divider sx={{ backgroundColor: 'grey.700', mb: 2 }} />
                  
                  <Box display="flex" alignItems="center" gap={3}>
                    <Box display="flex" alignItems="center" gap={1}>
                      <IconButton size="small" sx={{ color: 'grey.400' }}>
                        <ThumbUp fontSize="small" />
                      </IconButton>
                      <Typography variant="body2">{review.upvotes}</Typography>
                    </Box>
                    <Box display="flex" alignItems="center" gap={1}>
                      <IconButton size="small" sx={{ color: 'grey.400' }}>
                        <ThumbDown fontSize="small" />
                      </IconButton>
                      <Typography variant="body2">{review.downvotes}</Typography>
                    </Box>
                    <Typography variant="body2" color="grey.500" ml="auto">
                      {formatDate(review.createdat)}
                    </Typography>
                  </Box>
                </ReviewCard>
              ))}
            </Box>
          )}
        </Container>
      </Box>

      {/* Trailer Dialog */}
      <Dialog
        open={trailerOpen}
        onClose={() => setTrailerOpen(false)}
        maxWidth="lg"
        fullWidth
        PaperProps={{
          sx: {
            backgroundColor: 'black',
            minHeight: '60vh'
          }
        }}
      >
        <DialogContent sx={{ p: 0, position: 'relative' }}>
          <IconButton
            onClick={() => setTrailerOpen(false)}
            sx={{
              position: 'absolute',
              top: 8,
              right: 8,
              color: 'white',
              backgroundColor: 'rgba(0, 0, 0, 0.5)',
              zIndex: 1000,
              '&:hover': {
                backgroundColor: 'rgba(0, 0, 0, 0.7)'
              }
            }}
          >
            <Close />
          </IconButton>
          
          {selectedTrailer && (
            <Box sx={{ position: 'relative', paddingBottom: '56.25%', height: 0 }}>
              <iframe
                src={`https://www.youtube.com/embed/${selectedTrailer.url}?autoplay=1`}
                title={selectedTrailer.title}
                frameBorder="0"
                allow="accelerometer; autoplay; clipboard-write; encrypted-media; gyroscope; picture-in-picture"
                allowFullScreen
                style={{
                  position: 'absolute',
                  top: 0,
                  left: 0,
                  width: '100%',
                  height: '100%'
                }}
              />
            </Box>
          )}
        </DialogContent>
      </Dialog>

      {/* Snackbar for notifications */}
      <Snackbar
        open={snackbarOpen}
        autoHideDuration={3000}
        onClose={() => setSnackbarOpen(false)}
        anchorOrigin={{ vertical: 'bottom', horizontal: 'center' }}
      >
        <Alert
          onClose={() => setSnackbarOpen(false)}
          severity={snackbarMessage.includes('Error') || snackbarMessage.includes('Failed') || snackbarMessage.includes('Please log in') ? 'error' : 'success'}
          sx={{ width: '100%' }}
        >
          {snackbarMessage}
        </Alert>
      </Snackbar>
    </StyledContainer>
  );
}

export default MovieDetails;
