// MovieDetails.jsx
import React, { useState, useEffect } from "react";
import axios from "axios";
import { useParams } from "react-router-dom";
import {
  Box,
  Typography,
  Container,
  Grid,
  Card,
  CardContent,
  CardMedia,
  Chip,
  Rating,
  Button,
  Dialog,
  DialogContent,
  IconButton,
  Avatar,
  Divider,
  Paper,
  styled
} from "@mui/material";
import {
  PlayArrow,
  Close,
  Star,
  Schedule,
  CalendarToday,
  Language,
  ThumbUp,
  ThumbDown
} from "@mui/icons-material";
import url from "../constants/url";

const StyledContainer = styled(Container)`
  padding: 0;
  max-width: none !important;
`;

const HeroSection = styled(Box)`
  position: relative;
  min-height: 70vh;
  background-size: cover;
  background-position: center;
  display: flex;
  align-items: center;
  color: white;
`;

const HeroOverlay = styled(Box)`
  position: absolute;
  top: 0;
  left: 0;
  right: 0;
  bottom: 0;
  background: linear-gradient(
    45deg,
    rgba(0, 0, 0, 0.9) 0%,
    rgba(0, 0, 0, 0.7) 50%,
    rgba(0, 0, 0, 0.4) 100%
  );
`;

const ContentSection = styled(Container)`
  position: relative;
  z-index: 2;
  padding: 40px 20px;
`;

const TitleImage = styled('img')`
  max-height: 200px;
  width: auto;
  object-fit: contain;
  filter: drop-shadow(0 4px 20px rgba(255, 255, 255, 0.3));
  margin-bottom: 20px;
`;

const InfoCard = styled(Card)`
  background: rgba(30, 30, 30, 0.95);
  color: white;
  margin-bottom: 30px;
  backdrop-filter: blur(10px);
`;

const TrailerButton = styled(Button)`
  background: linear-gradient(45deg, #ff4081, #ff6ec7);
  color: white;
  padding: 12px 30px;
  border-radius: 25px;
  font-weight: bold;
  text-transform: none;
  margin: 20px 0;
  &:hover {
    background: linear-gradient(45deg, #e91e63, #ff4081);
    transform: translateY(-2px);
    box-shadow: 0 8px 25px rgba(255, 64, 129, 0.3);
  }
`;

const CastCard = styled(Card)`
  background: rgba(40, 40, 40, 0.9);
  color: white;
  height: 100%;
  transition: transform 0.3s ease;
  &:hover {
    transform: translateY(-5px);
  }
`;

const ReviewCard = styled(Paper)`
  background: rgba(50, 50, 50, 0.9);
  color: white;
  padding: 20px;
  margin-bottom: 15px;
  border-radius: 12px;
`;

function MovieDetails() {
  const { id } = useParams();
  const [movie, setMovie] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [trailerOpen, setTrailerOpen] = useState(false);
  const [selectedTrailer, setSelectedTrailer] = useState(null);

  useEffect(() => {
    axios
      .get(`${url}/api/movies/${id}`)
      .then((res) => {
        setMovie(res.data);
        setLoading(false);
      })
      .catch((err) => {
        console.error("Error fetching movie details:", err);
        setError("Movie not found");
        setLoading(false);
      });
  }, [id]);

  const handleTrailerPlay = (trailer) => {
    setSelectedTrailer(trailer);
    setTrailerOpen(true);
  };

  const formatDate = (dateString) => {
    return new Date(dateString).toLocaleDateString('en-US', {
      year: 'numeric',
      month: 'long',
      day: 'numeric'
    });
  };

  const formatDuration = (minutes) => {
    const hours = Math.floor(minutes / 60);
    const mins = minutes % 60;
    return hours > 0 ? `${hours}h ${mins}m` : `${mins}m`;
  };

  if (loading) {
    return (
      <Box display="flex" justifyContent="center" alignItems="center" minHeight="100vh">
        <Typography variant="h5" color="white">Loading...</Typography>
      </Box>
    );
  }

  if (error) {
    return (
      <Box display="flex" justifyContent="center" alignItems="center" minHeight="100vh">
        <Typography variant="h5" color="error">{error}</Typography>
      </Box>
    );
  }

  return (
    <StyledContainer>
      {/* Hero Section */}
      <HeroSection
        sx={{
          backgroundImage: `url(/img/${movie.posterimage})`,
        }}
      >
        <HeroOverlay />
        <ContentSection maxWidth="xl">
          <Grid container spacing={4} alignItems="center">
            <Grid item xs={12}>
              <Box textAlign="center">
                <TitleImage
                  src={`/img/${movie.titleimage}`}
                  alt={`${movie.title} Title`}
                />
                
                <InfoCard sx={{ maxWidth: '800px', margin: '0 auto 30px' }}>
                  <CardContent>
                    <Typography variant="h6" gutterBottom sx={{ color: '#f5c518' }}>
                      Synopsis
                    </Typography>
                    <Typography variant="body1" sx={{ lineHeight: 1.7 }}>
                      {movie.synopsis}
                    </Typography>
                  </CardContent>
                </InfoCard>
                
                <Box display="flex" justifyContent="center" alignItems="center" gap={2} mb={2}>
                  <Rating
                    value={movie.averagerating / 2}
                    precision={0.1}
                    readOnly
                    size="large"
                  />
                  <Typography variant="h5" sx={{ color: '#f5c518', fontWeight: 'bold' }}>
                    {movie.averagerating}/10
                  </Typography>
                </Box>

                <Box display="flex" justifyContent="center" gap={2} mb={3} flexWrap="wrap">
                  <Chip
                    icon={<Language />}
                    label={movie.language}
                    variant="outlined"
                    sx={{ color: 'white', borderColor: 'white' }}
                  />
                  <Chip
                    icon={<Schedule />}
                    label={formatDuration(movie.duration)}
                    variant="outlined"
                    sx={{ color: 'white', borderColor: 'white' }}
                  />
                  <Chip
                    icon={<CalendarToday />}
                    label={formatDate(movie.releasedate)}
                    variant="outlined"
                    sx={{ color: 'white', borderColor: 'white' }}
                  />
                </Box>

                {movie.trailers && movie.trailers.length > 0 && (
                  <TrailerButton
                    startIcon={<PlayArrow />}
                    onClick={() => handleTrailerPlay(movie.trailers[0])}
                    size="large"
                  >
                    Watch Trailer
                  </TrailerButton>
                )}
              </Box>
            </Grid>
          </Grid>
        </ContentSection>
      </HeroSection>

      {/* Synopsis Section */}
      <Box sx={{ backgroundColor: '#121212', py: 4 }}>
        <Container maxWidth="xl">
          <InfoCard sx={{ maxWidth: '800px', margin: '0 auto' }}>
            <CardContent>
              <Typography variant="h6" gutterBottom sx={{ color: '#f5c518' }}>
                Synopsis
              </Typography>
              <Typography variant="body1" sx={{ lineHeight: 1.7 }}>
                {movie.synopsis}
              </Typography>
            </CardContent>
          </InfoCard>
        </Container>
      </Box>

      {/* Details Section */}
      <Box sx={{ backgroundColor: '#121212', minHeight: '100vh', py: 4 }}>
        <Container maxWidth="xl">
          {/* Cast Section */}
          {movie.cast && movie.cast.length > 0 && (
            <Box mb={5}>
              <Typography variant="h4" gutterBottom sx={{ color: 'white', mb: 3 }}>
                Cast
              </Typography>
              <Grid container spacing={3}>
                {movie.cast.map((actor, index) => (
                  <Grid item xs={12} sm={6} md={4} lg={3} key={index}>
                    <CastCard>
                      <CardMedia
                        component="img"
                        height="300"
                        image={`/img/${actor.Image}`}
                        alt={actor.Name}
                        sx={{ objectFit: 'cover' }}
                      />
                      <CardContent>
                        <Typography variant="h6" gutterBottom>
                          {actor.Name}
                        </Typography>
                        <Typography variant="body2" color="grey.400" gutterBottom>
                          as {actor.RoleName}
                        </Typography>
                        <Box display="flex" alignItems="center" gap={1}>
                          <Star sx={{ color: '#f5c518', fontSize: 16 }} />
                          <Typography variant="body2">
                            {actor.CastRating}/10
                          </Typography>
                        </Box>
                        <Typography variant="body2" color="grey.500" mt={1}>
                          {actor.Nationality}  Born {formatDate(actor.DateOfBirth)}
                        </Typography>
                      </CardContent>
                    </CastCard>
                  </Grid>
                ))}
              </Grid>
            </Box>
          )}

          {/* Crew Section */}
          <Grid container spacing={4} mb={5}>
            {/* Directors */}
            {movie.directors && movie.directors.length > 0 && (
              <Grid item xs={12} md={6}>
                <Typography variant="h5" gutterBottom sx={{ color: 'white' }}>
                  Directors
                </Typography>
                {movie.directors.map((director, index) => (
                  <Card key={index} sx={{ backgroundColor: 'rgba(40, 40, 40, 0.9)', color: 'white', mb: 2 }}>
                    <CardContent>
                      <Box display="flex" alignItems="center" gap={2}>
                        <Avatar
                          src={`/img/${director.image}`}
                          alt={director.name}
                          sx={{ width: 60, height: 60 }}
                        />
                        <Box>
                          <Typography variant="h6">{director.name}</Typography>
                          <Typography variant="body2" color="grey.400">
                            {director.nationality}  Born {formatDate(director.dateofbirth)}
                          </Typography>
                          <Typography variant="body2" mt={1}>
                            {director.bio}
                          </Typography>
                        </Box>
                      </Box>
                    </CardContent>
                  </Card>
                ))}
              </Grid>
            )}

            {/* Writers */}
            {movie.writers && movie.writers.length > 0 && (
              <Grid item xs={12} md={6}>
                <Typography variant="h5" gutterBottom sx={{ color: 'white' }}>
                  Writers
                </Typography>
                {movie.writers.map((writer, index) => (
                  <Card key={index} sx={{ backgroundColor: 'rgba(40, 40, 40, 0.9)', color: 'white', mb: 2 }}>
                    <CardContent>
                      <Box display="flex" alignItems="center" gap={2}>
                        <Avatar
                          src={`/img/${writer.image}`}
                          alt={writer.name}
                          sx={{ width: 60, height: 60 }}
                        />
                        <Box>
                          <Typography variant="h6">{writer.name}</Typography>
                          <Typography variant="body2" color="grey.400">
                            {writer.nationality}  Born {formatDate(writer.dateofbirth)}
                          </Typography>
                          <Typography variant="body2" mt={1}>
                            {writer.bio}
                          </Typography>
                        </Box>
                      </Box>
                    </CardContent>
                  </Card>
                ))}
              </Grid>
            )}
          </Grid>

          {/* Studios */}
          {movie.studios && movie.studios.length > 0 && (
            <Box mb={5}>
              <Typography variant="h5" gutterBottom sx={{ color: 'white' }}>
                Production Studios
              </Typography>
              <Grid container spacing={3}>
                {movie.studios.map((studio, index) => (
                  <Grid item xs={12} sm={6} md={4} key={index}>
                    <Card sx={{ backgroundColor: 'rgba(40, 40, 40, 0.9)', color: 'white' }}>
                      <CardContent>
                        <Box display="flex" alignItems="center" gap={2} mb={2}>
                          <Avatar
                            src={`/img/${studio.logo}`}
                            alt={studio.name}
                            sx={{ width: 50, height: 50 }}
                          />
                          <Box>
                            <Typography variant="h6">{studio.name}</Typography>
                            <Typography variant="body2" color="grey.400">
                              Founded {studio.foundedyear}
                            </Typography>
                          </Box>
                        </Box>
                        {studio.website && (
                          <Button
                            variant="outlined"
                            size="small"
                            href={studio.website}
                            target="_blank"
                            sx={{ color: 'white', borderColor: 'white' }}
                          >
                            Visit Website
                          </Button>
                        )}
                      </CardContent>
                    </Card>
                  </Grid>
                ))}
              </Grid>
            </Box>
          )}

          {/* Reviews Section */}
          {movie.reviews && movie.reviews.length > 0 && (
            <Box>
              <Typography variant="h5" gutterBottom sx={{ color: 'white' }}>
                Reviews ({movie.reviews.length})
              </Typography>
              {movie.reviews.map((review, index) => (
                <ReviewCard key={index}>
                  <Box display="flex" justifyContent="space-between" alignItems="flex-start" mb={2}>
                    <Box>
                      <Typography variant="h6" gutterBottom>
                        {review.title}
                      </Typography>
                      <Box display="flex" alignItems="center" gap={1}>
                        <Rating value={review.ratingscore / 2} size="small" readOnly />
                        <Typography variant="body2">
                          {review.ratingscore}/10
                        </Typography>
                      </Box>
                    </Box>
                    {review.hasspoiler && (
                      <Chip
                        label="Spoiler"
                        color="warning"
                        size="small"
                      />
                    )}
                  </Box>
                  
                  <Typography variant="body1" mb={2} sx={{ lineHeight: 1.6 }}>
                    {review.content}
                  </Typography>
                  
                  <Divider sx={{ backgroundColor: 'grey.700', mb: 2 }} />
                  
                  <Box display="flex" alignItems="center" gap={3}>
                    <Box display="flex" alignItems="center" gap={1}>
                      <IconButton size="small" sx={{ color: 'grey.400' }}>
                        <ThumbUp fontSize="small" />
                      </IconButton>
                      <Typography variant="body2">{review.upvotes}</Typography>
                    </Box>
                    <Box display="flex" alignItems="center" gap={1}>
                      <IconButton size="small" sx={{ color: 'grey.400' }}>
                        <ThumbDown fontSize="small" />
                      </IconButton>
                      <Typography variant="body2">{review.downvotes}</Typography>
                    </Box>
                    <Typography variant="body2" color="grey.500" ml="auto">
                      {formatDate(review.createdat)}
                    </Typography>
                  </Box>
                </ReviewCard>
              ))}
            </Box>
          )}
        </Container>
      </Box>

      {/* Trailer Dialog */}
      <Dialog
        open={trailerOpen}
        onClose={() => setTrailerOpen(false)}
        maxWidth="lg"
        fullWidth
        PaperProps={{
          sx: {
            backgroundColor: 'black',
            minHeight: '60vh'
          }
        }}
      >
        <DialogContent sx={{ p: 0, position: 'relative' }}>
          <IconButton
            onClick={() => setTrailerOpen(false)}
            sx={{
              position: 'absolute',
              top: 8,
              right: 8,
              color: 'white',
              backgroundColor: 'rgba(0, 0, 0, 0.5)',
              zIndex: 1000,
              '&:hover': {
                backgroundColor: 'rgba(0, 0, 0, 0.7)'
              }
            }}
          >
            <Close />
          </IconButton>
          
          {selectedTrailer && (
            <Box sx={{ position: 'relative', paddingBottom: '56.25%', height: 0 }}>
              <iframe
                src={`https://www.youtube.com/embed/${selectedTrailer.url}?autoplay=1`}
                title={selectedTrailer.title}
                frameBorder="0"
                allow="accelerometer; autoplay; clipboard-write; encrypted-media; gyroscope; picture-in-picture"
                allowFullScreen
                style={{
                  position: 'absolute',
                  top: 0,
                  left: 0,
                  width: '100%',
                  height: '100%'
                }}
              />
            </Box>
          )}
        </DialogContent>
      </Dialog>
    </StyledContainer>
  );
}

export default MovieDetails;
