// UserReviewSection.jsx
import React, { useState, useEffect } from "react";
import axios from "axios";
import {
  Box,
  Container,
  Typography,
  Paper,
  styled,
  Button,
  TextField,
  FormControlLabel,
  Checkbox,
  Alert,
  IconButton,
} from "@mui/material";
import { Add, Star, StarBorder } from "@mui/icons-material";
import url from "../../constants/url";

const ReviewFormSection = styled(Box)`
  background-color: #1a1a1a;
  padding: 40px 0;
`;

const ReviewCard = styled(Paper)`
  background: rgba(50, 50, 50, 0.9);
  color: white;
  padding: 30px;
  border-radius: 12px;
  max-width: 800px;
  margin: 0 auto;
`;

const SubmitButton = styled(Button)`
  background: #1976d2;
  color: white;
  padding: 12px 32px;
  &:hover {
    background: #1565c0;
  }
`;

const StarRating = styled(Box)`
  display: flex;
  align-items: center;
  gap: 4px;
  margin-bottom: 16px;
`;

const RatingContainer = styled(Box)`
  display: flex;
  flex-direction: column;
  gap: 8px;
`;

const RatingDisplay = styled(Typography)`
  color: #ffd700;
  font-weight: bold;
  font-size: 1.2rem;
`;

function UserReviewSection({ movieId, isAuthenticated }) {
  const [showReviewForm, setShowReviewForm] = useState(false);
  const [userHasReviewed, setUserHasReviewed] = useState(false);
  const [loading, setLoading] = useState(false);
  const [checkingReview, setCheckingReview] = useState(false);
  const [reviewForm, setReviewForm] = useState({
    title: '',
    content: '',
    rating: 5,
    hasSpoiler: false
  });
  const [hoverRating, setHoverRating] = useState(0);

  // Check if user has already reviewed this movie
  const checkUserReviewStatus = async () => {
    if (!isAuthenticated || !movieId) return;

    setCheckingReview(true);
    try {
      const token = localStorage.getItem('token');
      const response = await axios.get(`${url}/api/reviews/user/check/${movieId}`, {
        headers: {
          'Authorization': `Bearer ${token}`
        }
      });
      setUserHasReviewed(response.data.hasReviewed);
    } catch (error) {
      console.error('Error checking user review status:', error);
      if (error.response?.status === 401) {
        localStorage.removeItem('token');
        localStorage.removeItem('user');
      }
    } finally {
      setCheckingReview(false);
    }
  };

  useEffect(() => {
    checkUserReviewStatus();
  }, [movieId, isAuthenticated]);

  const handleSubmitReview = async () => {
    // Validation
    if (!reviewForm.title.trim() || !reviewForm.content.trim()) {
      alert('Please fill in all fields');
      return;
    }

    if (!isAuthenticated) {
      alert('Please log in to submit a review');
      return;
    }

    if (!movieId) {
      alert('Movie ID is missing');
      return;
    }

    // Convert movieId to integer
    const movieIdInt = parseInt(movieId, 10);
    if (isNaN(movieIdInt)) {
      alert('Invalid movie ID');
      return;
    }

    console.log('Submitting review with data:', {
      movieId: movieIdInt,
      title: reviewForm.title.trim(),
      content: reviewForm.content.trim(),
      rating: reviewForm.rating,
      hasSpoiler: reviewForm.hasSpoiler
    });

    setLoading(true);
    try {
      const token = localStorage.getItem('token');
      
      const reviewData = {
        movieId: movieIdInt,
        title: reviewForm.title.trim(),
        content: reviewForm.content.trim(),
        rating: reviewForm.rating, // Integer from 1-10
        hasSpoiler: reviewForm.hasSpoiler
      };

      const response = await axios.post(`${url}/api/reviews`, reviewData, {
        headers: {
          'Authorization': `Bearer ${token}`,
          'Content-Type': 'application/json'
        }
      });

      console.log('Review submitted successfully:', response.data);

      // Reset form and hide it
      setReviewForm({ title: '', content: '', rating: 5, hasSpoiler: false });
      setShowReviewForm(false);
      setUserHasReviewed(true);
      
      // Show success message
      alert('Review submitted successfully!');
      
      // Refresh the page to show new review
      window.location.reload();
      
    } catch (error) {
      console.error('Error submitting review:', error);
      console.error('Error response:', error.response?.data);
      
      let errorMessage = 'Failed to submit review';
      
      if (error.response?.status === 400) {
        if (error.response.data?.message) {
          errorMessage = error.response.data.message;
        } else {
          errorMessage = 'Bad request - check your review data';
        }
        // If it's a duplicate review error, update the state
        if (errorMessage.toLowerCase().includes('already reviewed')) {
          setUserHasReviewed(true);
        }
      } else if (error.response?.status === 401) {
        errorMessage = 'Please log in to submit a review';
        localStorage.removeItem('token');
        localStorage.removeItem('user');
      } else if (error.response?.status === 404) {
        errorMessage = 'Movie not found';
      } else if (error.response?.status === 422) {
        errorMessage = 'Invalid review data format';
      }
      
      alert(errorMessage);
    } finally {
      setLoading(false);
    }
  };

  const handleStarClick = (rating) => {
    setReviewForm(prev => ({ ...prev, rating }));
  };

  const handleStarHover = (rating) => {
    setHoverRating(rating);
  };

  const handleStarLeave = () => {
    setHoverRating(0);
  };

  const renderStars = () => {
    const stars = [];
    const displayRating = hoverRating || reviewForm.rating;

    for (let i = 1; i <= 10; i++) {
      stars.push(
        <IconButton
          key={i}
          onClick={() => handleStarClick(i)}
          onMouseEnter={() => handleStarHover(i)}
          onMouseLeave={handleStarLeave}
          sx={{
            color: i <= displayRating ? '#ffd700' : '#666',
            padding: '4px',
            '&:hover': {
              color: '#ffd700'
            }
          }}
        >
          {i <= displayRating ? <Star /> : <StarBorder />}
        </IconButton>
      );
    }
    return stars;
  };

  if (!isAuthenticated) {
    return null; // Don't show anything if user is not authenticated
  }

  if (checkingReview) {
    return (
      <ReviewFormSection>
        <Container maxWidth="xl">
          <Typography variant="h5" sx={{ color: 'white', textAlign: 'center' }}>
            Checking review status...
          </Typography>
        </Container>
      </ReviewFormSection>
    );
  }

  return (
    <ReviewFormSection>
      <Container maxWidth="xl">
        {!userHasReviewed && !showReviewForm && (
          <Box display="flex" justifyContent="center">
            <Button
              variant="contained"
              startIcon={<Add />}
              onClick={() => setShowReviewForm(true)}
              sx={{ 
                background: '#1976d2', 
                color: 'white',
                padding: '12px 24px',
                fontSize: '1.1rem'
              }}
            >
              Write a Review
            </Button>
          </Box>
        )}

        {userHasReviewed && !showReviewForm && (
          <Box display="flex" justifyContent="center">
            <Alert severity="info" sx={{ maxWidth: '600px' }}>
              You have already submitted a review for this movie.
            </Alert>
          </Box>
        )}

        {showReviewForm && !userHasReviewed && (
          <ReviewCard>
            <Typography variant="h5" gutterBottom sx={{ textAlign: 'center', mb: 3 }}>
              Write Your Review
            </Typography>
            
            <TextField
              fullWidth
              label="Review Title"
              value={reviewForm.title}
              onChange={(e) => setReviewForm(prev => ({ ...prev, title: e.target.value }))}
              sx={{ 
                mb: 3, 
                '& .MuiInputLabel-root': { color: 'white' }, 
                '& .MuiOutlinedInput-root': { 
                  color: 'white',
                  '& fieldset': {
                    borderColor: 'rgba(255, 255, 255, 0.3)'
                  },
                  '&:hover fieldset': {
                    borderColor: 'rgba(255, 255, 255, 0.5)'
                  },
                  '&.Mui-focused fieldset': {
                    borderColor: '#1976d2'
                  }
                }
              }}
            />

            <RatingContainer mb={3}>
              <Typography component="legend" sx={{ color: 'white', mb: 1, fontSize: '1.1rem' }}>
                Rating
              </Typography>
              <StarRating>
                {renderStars()}
                <RatingDisplay>
                  {hoverRating || reviewForm.rating}/10
                </RatingDisplay>
              </StarRating>
            </RatingContainer>

            <TextField
              fullWidth
              multiline
              rows={6}
              label="Review Content"
              placeholder="Share your thoughts about this movie..."
              value={reviewForm.content}
              onChange={(e) => setReviewForm(prev => ({ ...prev, content: e.target.value }))}
              sx={{ 
                mb: 3, 
                '& .MuiInputLabel-root': { color: 'white' }, 
                '& .MuiOutlinedInput-root': { 
                  color: 'white',
                  '& fieldset': {
                    borderColor: 'rgba(255, 255, 255, 0.3)'
                  },
                  '&:hover fieldset': {
                    borderColor: 'rgba(255, 255, 255, 0.5)'
                  },
                  '&.Mui-focused fieldset': {
                    borderColor: '#1976d2'
                  }
                }
              }}
            />

            <FormControlLabel
              control={
                <Checkbox
                  checked={reviewForm.hasSpoiler}
                  onChange={(e) => setReviewForm(prev => ({ ...prev, hasSpoiler: e.target.checked }))}
                  sx={{ 
                    color: 'white',
                    '&.Mui-checked': {
                      color: '#ff9800'
                    }
                  }}
                />
              }
              label="This review contains spoilers"
              sx={{ color: 'white', mb: 3 }}
            />

            <Box display="flex" justifyContent="center" gap={2}>
              <SubmitButton 
                onClick={handleSubmitReview}
                disabled={loading}
              >
                {loading ? 'Submitting...' : 'Submit Review'}
              </SubmitButton>
              <Button
                variant="outlined"
                onClick={() => setShowReviewForm(false)}
                disabled={loading}
                sx={{ 
                  color: 'white', 
                  borderColor: 'white',
                  padding: '12px 32px',
                  '&:hover': {
                    borderColor: 'rgba(255, 255, 255, 0.8)',
                    backgroundColor: 'rgba(255, 255, 255, 0.1)'
                  }
                }}
              >
                Cancel
              </Button>
            </Box>
          </ReviewCard>
        )}
      </Container>
    </ReviewFormSection>
  );
}

export default UserReviewSection;
