import React, { useState } from "react";
import { useNavigate } from "react-router-dom";
import { Box, Typography, TextField, Button, styled, Paper, Tabs, Tab } from "@mui/material";
import axios from "axios";

const Wrapper = styled(Paper)`
  max-width: 400px;
  margin: 80px auto;
  padding: 40px;
  background-color: #1c1c1c;
  color: white;
`;

const StyledInput = styled(TextField)`
  margin-bottom: 20px;
  & .MuiInputBase-input {
    color: #d3d3d3; /* Grey for input text */
    background-color: #1c1c1c; /* Black background */
  }
  & .MuiInputLabel-root {
    color: gray;
  }
  & .MuiOutlinedInput-root .MuiOutlinedInput-notchedOutline {
    border-color: gray;
  }
  & .MuiOutlinedInput-root.Mui-focused .MuiOutlinedInput-notchedOutline {
    border-color: gray; /* Keep border grey when focused */
  }
  & .MuiOutlinedInput-root:hover .MuiOutlinedInput-notchedOutline {
    border-color: gray; /* Keep border grey on hover */
  }
  & .MuiOutlinedInput-root.Mui-focused {
    background-color: #1c1c1c; /* Maintain black background when focused */
  }
  & .MuiOutlinedInput-root.Mui-filled {
    background-color: #1c1c1c; /* Maintain black background when filled */
  }
`;

const StyledTabs = styled(Tabs)`
  margin-bottom: 20px; /* Adds line space below tabs */
`;

const SignInSignUp = () => {
  const navigate = useNavigate();
  const [tab, setTab] = useState(0);
  const [form, setForm] = useState({ username: "", email: "", password: "" });

  const handleChange = (e) => {
    setForm({ ...form, [e.target.name]: e.target.value });
  };

  const handleSubmit = async () => {
    try {
      const url = tab === 0 ? "/api/auth/signin" : "/api/auth/signup";
      const { data } = await axios.post(url, form);
      localStorage.setItem("user", JSON.stringify(data.user));
      navigate("/");
    } catch (err) {
      alert("Authentication failed");
    }
  };

  return (
    <Wrapper>
      <StyledTabs value={tab} onChange={(_, newVal) => setTab(newVal)} textColor="inherit" indicatorColor="primary">
        <Tab label="Sign In" />
        <Tab label="Sign Up" />
      </StyledTabs>

      {tab === 1 && (
        <StyledInput label="Email" variant="outlined" fullWidth name="email" onChange={handleChange} />
      )}
      <StyledInput label="Username" variant="outlined" fullWidth name="username" onChange={handleChange} />
      <StyledInput label="Password" type="password" variant="outlined" fullWidth name="password" onChange={handleChange} />

      <Button variant="contained" color="primary" fullWidth onClick={handleSubmit}>
        {tab === 0 ? "Sign In" : "Sign Up"}
      </Button>
    </Wrapper>
  );
};

export default SignInSignUp;
