import {
    Container,
    Paper,
    Typography,
    TextField,
    FormControl,
    InputLabel,
    Select,
    MenuItem,
    Chip,
    Box,
    Button,
    Grid,
    Slider,
    Card,
    CardMedia,
    CardContent,
    Rating,
    CircularProgress,
    Alert,
    Autocomplete,
    FormControlLabel,
    Switch,
    Divider
} from '@mui/material';
import {
    Search,
    FilterList,
    Clear,
    Movie,
    CalendarToday,
    Star,
    Category
} from '@mui/icons-material';
import { styled } from '@mui/material/styles';

// Styled components
const StyledPaper = styled(Paper)(({ theme }) => ({
    padding: theme.spacing(3),
    marginBottom: theme.spacing(3),
    borderRadius: 16,
    boxShadow: '0 8px 32px rgba(0, 0, 0, 0.08)',
    border: `1px solid ${theme.palette.divider}`,
}));

const FilterSection = styled(Box)(({ theme }) => ({
    marginBottom: theme.spacing(2.5),
    '& .MuiFormControl-root': {
        minWidth: 200,
    },
}));

const SearchButton = styled(Button)(({ theme }) => ({
    borderRadius: 25,
    padding: '12px 32px',
    textTransform: 'none',
    fontSize: '1rem',
    fontWeight: 600,
    boxShadow: '0 4px 16px rgba(25, 118, 210, 0.3)',
    '&:hover': {
        boxShadow: '0 6px 20px rgba(25, 118, 210, 0.4)',
        transform: 'translateY(-2px)',
    },
    transition: 'all 0.3s ease',
}));

const MovieCard = styled(Card)(({ theme }) => ({
    height: '100%',
    borderRadius: 12,
    transition: 'all 0.3s ease',
    cursor: 'pointer',
    '&:hover': {
        transform: 'translateY(-4px)',
        boxShadow: '0 12px 32px rgba(0, 0, 0, 0.15)',
    },
}));

const AdvancedSearch = () => {
    // Search filters state
    const [filters, setFilters] = useState({
        query: '',
        genres: [],
        year: [1980, new Date().getFullYear()],
        rating: [0, 10],
        sortBy: 'popularity',
        adult: false,
        language: 'en'
    });

    // UI state
    const [searchResults, setSearchResults] = useState([]);
    const [loading, setLoading] = useState(false);
    const [error, setError] = useState('');
    const [hasSearched, setHasSearched] = useState(false);

    // Available options (these would typically come from your API)
    const genres = [
        'Action', 'Adventure', 'Animation', 'Comedy', 'Crime', 'Documentary',
        'Drama', 'Family', 'Fantasy', 'History', 'Horror', 'Music', 'Mystery',
        'Romance', 'Science Fiction', 'TV Movie', 'Thriller', 'War', 'Western'
    ];

    const sortOptions = [
        { value: 'popularity', label: 'Popularity' },
        { value: 'release_date', label: 'Release Date' },
        { value: 'rating', label: 'Rating' },
        { value: 'title', label: 'Title (A-Z)' },
        { value: 'revenue', label: 'Revenue' }
    ];

    const languages = [
        { value: 'en', label: 'English' },
        { value: 'es', label: 'Spanish' },
        { value: 'fr', label: 'French' },
        { value: 'de', label: 'German' },
        { value: 'it', label: 'Italian' },
        { value: 'ja', label: 'Japanese' },
        { value: 'ko', label: 'Korean' },
        { value: 'zh', label: 'Chinese' }
    ];

    // Handle filter changes
    const handleFilterChange = (filterName, value) => {
        setFilters(prev => ({
            ...prev,
            [filterName]: value
        }));
    };

    // Mock search function (replace with your actual API call)
    const performSearch = async () => {
        setLoading(true);
        setError('');
        setHasSearched(true);

        try {
            // Simulate API call delay
            await new Promise(resolve => setTimeout(resolve, 1500));

            // Mock search results (replace with actual API call)
            const mockResults = [
                {
                    id: 1,
                    title: 'Sample Movie 1',
                    poster_path: '/sample1.jpg',
                    release_date: '2023-06-15',
                    vote_average: 8.2,
                    overview: 'This is a sample movie description for testing purposes.',
                    genre_ids: [28, 12] // Action, Adventure
                },
                {
                    id: 2,
                    title: 'Sample Movie 2',
                    poster_path: '/sample2.jpg',
                    release_date: '2022-11-20',
                    vote_average: 7.5,
                    overview: 'Another sample movie for the advanced search demo.',
                    genre_ids: [35, 18] // Comedy, Drama
                }
            ];

            setSearchResults(mockResults);
        } catch (err) {
            setError('Failed to search movies. Please try again.');
        } finally {
            setLoading(false);
        }
    };

    // Clear all filters
    const clearFilters = () => {
        setFilters({
            query: '',
            genres: [],
            year: [1980, new Date().getFullYear()],
            rating: [0, 10],
            sortBy: 'popularity',
            adult: false,
            language: 'en'
        });
        setSearchResults([]);
        setHasSearched(false);
        setError('');
    };

    return (
        <Container maxWidth="lg" sx={{ py: 4 }}>
            {/* Header */}
            <Box sx={{ textAlign: 'center', mb: 4 }}>
                <Typography 
                    variant="h3" 
                    component="h1" 
                    gutterBottom
                    sx={{ 
                        fontWeight: 700,
                        background: 'linear-gradient(45deg, #1976d2, #42a5f5)',
                        backgroundClip: 'text',
                        WebkitBackgroundClip: 'text',
                        color: 'transparent',
                        mb: 2
                    }}
                >
                    Advanced Movie Search
                </Typography>
                <Typography variant="h6" color="text.secondary" sx={{ mb: 3 }}>
                    Find movies with powerful filters and sorting options
                </Typography>
            </Box>

            {/* Search Filters */}
            <StyledPaper>
                <Box sx={{ display: 'flex', alignItems: 'center', mb: 3 }}>
                    <FilterList sx={{ mr: 2, color: 'primary.main' }} />
                    <Typography variant="h5" fontWeight={600}>
                        Search Filters
                    </Typography>
                </Box>

                <Grid container spacing={3}>
                    {/* Search Query */}
                    <Grid item xs={12} md={6}>
                        <FilterSection>
                            <TextField
                                fullWidth
                                label="Movie Title"
                                variant="outlined"
                                value={filters.query}
                                onChange={(e) => handleFilterChange('query', e.target.value)}
                                InputProps={{
                                    startAdornment: <Movie sx={{ mr: 1, color: 'action.active' }} />
                                }}
                                placeholder="Enter movie title..."
                            />
                        </FilterSection>
                    </Grid>

                    {/* Language */}
                    <Grid item xs={12} md={6}>
                        <FilterSection>
                            <FormControl fullWidth>
                                <InputLabel>Language</InputLabel>
                                <Select
                                    value={filters.language}
                                    label="Language"
                                    onChange={(e) => handleFilterChange('language', e.target.value)}
                                >
                                    {languages.map((lang) => (
                                        <MenuItem key={lang.value} value={lang.value}>
                                            {lang.label}
                                        </MenuItem>
                                    ))}
                                </Select>
                            </FormControl>
                        </FilterSection>
                    </Grid>

                    {/* Genres */}
                    <Grid item xs={12}>
                        <FilterSection>
                            <Autocomplete
                                multiple
                                options={genres}
                                value={filters.genres}
                                onChange={(event, newValue) => handleFilterChange('genres', newValue)}
                                renderTags={(value, getTagProps) =>
                                    value.map((option, index) => (
                                        <Chip 
                                            variant="outlined" 
                                            label={option} 
                                            {...getTagProps({ index })} 
                                            key={option}
                                            sx={{ borderRadius: 2 }}
                                        />
                                    ))
                                }
                                renderInput={(params) => (
                                    <TextField
                                        {...params}
                                        label="Genres"
                                        placeholder="Select genres..."
                                        InputProps={{
                                            ...params.InputProps,
                                            startAdornment: (
                                                <>
                                                    <Category sx={{ mr: 1, color: 'action.active' }} />
                                                    {params.InputProps.startAdornment}
                                                </>
                                            )
                                        }}
                                    />
                                )}
                            />
                        </FilterSection>
                    </Grid>

                    {/* Year Range */}
                    <Grid item xs={12} md={6}>
                        <FilterSection>
                            <Typography gutterBottom sx={{ display: 'flex', alignItems: 'center' }}>
                                <CalendarToday sx={{ mr: 1, fontSize: 20 }} />
                                Release Year: {filters.year[0]} - {filters.year[1]}
                            </Typography>
                            <Slider
                                value={filters.year}
                                onChange={(event, newValue) => handleFilterChange('year', newValue)}
                                valueLabelDisplay="auto"
                                min={1900}
                                max={new Date().getFullYear()}
                                sx={{ mt: 2 }}
                            />
                        </FilterSection>
                    </Grid>

                    {/* Rating Range */}
                    <Grid item xs={12} md={6}>
                        <FilterSection>
                            <Typography gutterBottom sx={{ display: 'flex', alignItems: 'center' }}>
                                <Star sx={{ mr: 1, fontSize: 20 }} />
                                Rating: {filters.rating[0]} - {filters.rating[1]}
                            </Typography>
                            <Slider
                                value={filters.rating}
                                onChange={(event, newValue) => handleFilterChange('rating', newValue)}
                                valueLabelDisplay="auto"
                                min={0}
                                max={10}
                                step={0.1}
                                sx={{ mt: 2 }}
                            />
                        </FilterSection>
                    </Grid>

                    {/* Sort By */}
                    <Grid item xs={12} md={6}>
                        <FilterSection>
                            <FormControl fullWidth>
                                <InputLabel>Sort By</InputLabel>
                                <Select
                                    value={filters.sortBy}
                                    label="Sort By"
                                    onChange={(e) => handleFilterChange('sortBy', e.target.value)}
                                >
                                    {sortOptions.map((option) => (
                                        <MenuItem key={option.value} value={option.value}>
                                            {option.label}
                                        </MenuItem>
                                    ))}
                                </Select>
                            </FormControl>
                        </FilterSection>
                    </Grid>

                    {/* Adult Content Toggle */}
                    <Grid item xs={12} md={6}>
                        <FilterSection>
                            <FormControlLabel
                                control={
                                    <Switch
                                        checked={filters.adult}
                                        onChange={(e) => handleFilterChange('adult', e.target.checked)}
                                    />
                                }
                                label="Include Adult Content"
                            />
                        </FilterSection>
                    </Grid>
                </Grid>

                <Divider sx={{ my: 3 }} />

                {/* Action Buttons */}
                <Box sx={{ display: 'flex', gap: 2, justifyContent: 'center' }}>
                    <SearchButton
                        variant="contained"
                        size="large"
                        onClick={performSearch}
                        startIcon={<Search />}
                        disabled={loading}
                    >
                        {loading ? 'Searching...' : 'Search Movies'}
                    </SearchButton>
                    <Button
                        variant="outlined"
                        size="large"
                        onClick={clearFilters}
                        startIcon={<Clear />}
                        sx={{ borderRadius: 25, px: 3 }}
                    >
                        Clear Filters
                    </Button>
                </Box>
            </StyledPaper>

            {/* Search Results */}
            {loading && (
                <Box sx={{ display: 'flex', justifyContent: 'center', py: 4 }}>
                    <CircularProgress size={60} />
                </Box>
            )}

            {error && (
                <Alert severity="error" sx={{ mb: 3, borderRadius: 2 }}>
                    {error}
                </Alert>
            )}

            {hasSearched && !loading && searchResults.length === 0 && !error && (
                <Alert severity="info" sx={{ mb: 3, borderRadius: 2 }}>
                    No movies found matching your criteria. Try adjusting your filters.
                </Alert>
            )}

            {searchResults.length > 0 && (
                <StyledPaper>
                    <Typography variant="h5" fontWeight={600} gutterBottom>
                        Search Results ({searchResults.length} movies found)
                    </Typography>
                    <Grid container spacing={3}>
                        {searchResults.map((movie) => (
                            <Grid item xs={12} sm={6} md={4} lg={3} key={movie.id}>
                                <MovieCard>
                                    <CardMedia
                                        component="img"
                                        height="300"
                                        image={movie.poster_path || '/api/placeholder/300/450'}
                                        alt={movie.title}
                                        sx={{ objectFit: 'cover' }}
                                    />
                                    <CardContent>
                                        <Typography variant="h6" fontWeight={600} gutterBottom noWrap>
                                            {movie.title}
                                        </Typography>
                                        <Box sx={{ display: 'flex', alignItems: 'center', mb: 1 }}>
                                            <Rating
                                                value={movie.vote_average / 2}
                                                precision={0.1}
                                                size="small"
                                                readOnly
                                            />
                                            <Typography variant="body2" color="text.secondary" sx={{ ml: 1 }}>
                                                {movie.vote_average}/10
                                            </Typography>
                                        </Box>
                                        <Typography variant="body2" color="text.secondary" gutterBottom>
                                            {movie.release_date && new Date(movie.release_date).getFullYear()}
                                        </Typography>
                                        <Typography 
                                            variant="body2" 
                                            color="text.secondary"
                                            sx={{ 
                                                display: '-webkit-box',
                                                WebkitLineClamp: 3,
                                                WebkitBoxOrient: 'vertical',
                                                overflow: 'hidden'
                                            }}
                                        >
                                            {movie.overview}
                                        </Typography>
                                    </CardContent>
                                </MovieCard>
                            </Grid>
                        ))}
                    </Grid>
                </StyledPaper>
            )}
        </Container>
    );
};

export default AdvancedSearch;
