// App.js
import './App.css';
import React, { useState, useEffect } from 'react';
import Home from './pages/Home'
import CategoryMovies from './pages/CategoryMovies';
import MovieDetails from './pages/MovieDetails'
import SignInSignUp from './pages/SignInSignUp';
import Watchlist from './pages/Watchlist';

import { BrowserRouter as Router, Routes, Route } from 'react-router-dom';
import Header from './components/common/Header';

import { routePath } from './constants/route';

function App() {
  const [user, setUser] = useState(() => {
    try {
      const storedUser = localStorage.getItem('user');
      return storedUser ? JSON.parse(storedUser) : null;
    } catch {
      localStorage.removeItem('user');
      return null;
    }
  });

  // Listen for user changes across the app
  useEffect(() => {
    const handleStorageChange = () => {
      try {
        const storedUser = localStorage.getItem('user');
        setUser(storedUser ? JSON.parse(storedUser) : null);
      } catch {
        localStorage.removeItem('user');
        setUser(null);
      }
    };

    // Listen for storage events and custom user events
    window.addEventListener('storage', handleStorageChange);
    window.addEventListener('userChanged', handleStorageChange);

    return () => {
      window.removeEventListener('storage', handleStorageChange);
      window.removeEventListener('userChanged', handleStorageChange);
    };
  }, []);

  return (
    <Router>
      <Header user={user} setUser={setUser} />
      <Routes>
        <Route path={routePath.home} element={<Home />} />
        <Route path={routePath.categories} element={<CategoryMovies />} />
        <Route path={routePath.movieDetails} element={<MovieDetails />} />
        <Route path={routePath.invalid} element={<Home />} />
        <Route path="/auth" element={<SignInSignUp setUser={setUser} />} />
        <Route path="/watchlist" element={<Watchlist />} />
      </Routes>
    </Router>
  );
}

export default App;
