import { useState } from 'react';
import { AppBar, Toolbar, styled, Box, Typography, InputBase } from '@mui/material';
import MenuIcon from '@mui/icons-material/Menu';
import BookmarkAddIcon from '@mui/icons-material/BookmarkAdd';
import ExpandMoreIcon from '@mui/icons-material/ExpandMore';
import { logoURL } from '../../constants/constant';
import { useNavigate } from 'react-router-dom';
import { routePath } from '../../constants/route';
import HeaderMenu from './HeaderMenu';

const StyledToolBar = styled(Toolbar)`
  background: #121212;
  min-height: 56px !important;
  padding: 0 115px !important;
  justify-content: space-between;
  & > div {
    display: flex;
    align-items: center;
    cursor: pointer;
    & > * {
      padding: 0 2px;
    }
    & > p {
      font-size: 14px;
      font-weight: 600;
    }
  }
  & > p {
    font-size: 14px;
    font-weight: 600;
  }
`;

const InputSearchField = styled(InputBase)`
  background: #FFFFFF;
  height: 30px;
  width: 50%;
  border-radius: 5px;
  padding-left: 10px;
`;

const Logo = styled('img')({
  width: 64
});

const Header = () => {
  const [open, setOpen] = useState(null);
  const [searchQuery, setSearchQuery] = useState('');
  const navigate = useNavigate();

  // ✅ Safe parse for user
  let user = null;
  try {
    const storedUser = localStorage.getItem('user');
    user = storedUser ? JSON.parse(storedUser) : null;
  } catch (error) {
    console.error('Error parsing user from localStorage:', error);
    localStorage.removeItem('user'); // optional: clear corrupted entry
  }

  const handleClick = (e) => {
    setOpen(e.currentTarget);
  };

  const handleClose = () => {
    setOpen(null);
  };

  const handleAuth = () => {
    if (user) {
      localStorage.removeItem('user');
      navigate('/');
    } else {
      navigate('/auth');
    }
  };

  const handleSearch = (e) => {
    if (e.key === 'Enter') {
      navigate(`/search?q=${searchQuery}`);
    }
  };

  return (
    <AppBar position='static'>
      <StyledToolBar>
        <Logo src={logoURL} alt="logo" onClick={() => navigate(routePath.home)} />

        <Box onClick={handleClick}>
          <MenuIcon />
          <Typography>Menu</Typography>
        </Box>

        <HeaderMenu open={open} handleClose={handleClose} />

        <InputSearchField
          placeholder="Search movies..."
          value={searchQuery}
          onChange={(e) => setSearchQuery(e.target.value)}
          onKeyDown={handleSearch}
        />

        <Box onClick={() => navigate('/watchlist')}>
          <BookmarkAddIcon />
          <Typography>Watchlist</Typography>
        </Box>

        <Typography onClick={handleAuth} sx={{ cursor: 'pointer' }}>
          {user ? 'Sign Out' : 'Sign In'}
        </Typography>

        <Box>
          <Typography>EN</Typography>
          <ExpandMoreIcon />
        </Box>
      </StyledToolBar>
    </AppBar>
  );
};

export default Header;
