import Header from '../components/common/Header';
import { categoryMovies } from '../services/api';

import { Box, styled, Typography, Divider } from '@mui/material';
import { useState, useEffect } from 'react';

import { useLocation } from 'react-router-dom';

import MoviesList from '../components/common/MoviesList';

import { POPULAR_API_URL, UPCOMING_API_URL, TOPRATED_API_URL, moviesType } from '../constants/constant';

import Carousel from 'react-multi-carousel';
import 'react-multi-carousel/lib/styles.css';

const responsive = {
    desktop: {
        breakpoint: { max: 3000, min: 1024 },
        items: 1,
        slidesToSlide: 1
    },
    tablet: {
        breakpoint: { max: 1024, min: 464 },
        items: 1,
        slidesToSlide: 1
    },
    mobile: {
        breakpoint: { max: 464, min: 0 },
        items: 1,
        slidesToSlide: 1
    }
};

const StyledBanner = styled('img')({
    height: 450,
    width: '100%',
    objectFit: 'cover'
});


const Component = styled(Box)`
   width: 80%;
   margin: auto;
`;

const  Container = styled(Box)`
   background: #F5F5F5;
   padding: 10px;
`




const CategoryMovies = () => {
    const [movies, setMovies] = useState([]);

    const { search } = useLocation();

    useEffect(() => {
        const getData = async (API_URL) => {
            const response = await categoryMovies(API_URL);
            setMovies(response.results || []);

        };

        let API_URL; 

        if (search.includes('popular'))
        {
           API_URL = POPULAR_API_URL;
        }
        else if (search.includes('upcoming'))
        {
           API_URL = UPCOMING_API_URL;
        }
        else if (search.includes('toprated'))
        {
          API_URL = TOPRATED_API_URL;
        }
        getData(API_URL);
    }, [search]); 

    return (
        <>
            <Header />
            <Component>
                <Carousel
                    responsive={responsive}
                    swipeable={false}
                    draggable={false}
                    showDots={false}
                    infinite={true}
                    autoPlay={true}
                    autoPlaySpeed={3000}
                    keyBoardControl={true}
                    slidesToSlide={1}
                >
                    {movies.map(movie => (
                        <Box key={movie.id}>
                            <StyledBanner
                                src={`https://image.tmdb.org/t/p/original/${movie.backdrop_path}`}
                                alt={movie.original_title}
                            />
                        </Box>
                    ))}
                </Carousel>
                <Container>
                    <Typography variant='h6'>
                        IMDb Charts
                    </Typography>
                    <Typography variant='h4'>IMDb {moviesType[search.split('=')[1]]} Movies</Typography>
                    <Typography style={{ fontSize: 12, margin}}>
                        IIMDb top {movies.length} as rated by regular IMDb voters.
                    </Typography>
                    <Divider />
                    <MoviesList movies={movies} />
                </Container>
            </Component>
            
        </>
    );
};

export default CategoryMovies;
