// SignInSignUp.jsx
import React, { useState, useEffect } from "react";
import { useNavigate } from "react-router-dom";
import { Box, Typography, TextField, Button, styled, Paper, Tabs, Tab, Alert } from "@mui/material";
import { useAuth } from "../components/contexts/AuthContext";

const Wrapper = styled(Paper)`
  max-width: 400px;
  margin: 80px auto;
  padding: 40px;
  background-color: #1c1c1c;
  color: white;
`;

const StyledInput = styled(TextField)`
  margin-bottom: 20px;
  & .MuiInputBase-input {
    color: #d3d3d3;
    background-color: #1c1c1c;
  }
  & .MuiInputLabel-root {
    color: gray;
  }
  & .MuiOutlinedInput-root .MuiOutlinedInput-notchedOutline {
    border-color: gray;
  }
  & .MuiOutlinedInput-root.Mui-focused .MuiOutlinedInput-notchedOutline {
    border-color: gray;
  }
  & .MuiOutlinedInput-root:hover .MuiOutlinedInput-notchedOutline {
    border-color: gray;
  }
  & .MuiOutlinedInput-root.Mui-focused {
    background-color: #1c1c1c;
  }
  & .MuiOutlinedInput-root.Mui-filled {
    background-color: #1c1c1c;
  }
`;

const StyledTabs = styled(Tabs)`
  margin-bottom: 20px;
`;

const OTPContainer = styled(Box)`
  display: flex;
  gap: 10px;
  align-items: center;
  margin-bottom: 20px;
`;

const ResendButton = styled(Button)`
  font-size: 12px;
  padding: 4px 8px;
  min-width: auto;
  color: white;
  background-color: #333333;
  border: 1px solid #555555;
  
  &:hover {
    background-color: #444444;
    border-color: #666666;
  }
  
  &:disabled {
    color: #666666;
    background-color: #2a2a2a;
    border-color: #444444;
  }
`;

const SignInSignUp = () => {
  const navigate = useNavigate();
  const { login, isAuthenticated } = useAuth(); 
  const [tab, setTab] = useState(0);
  const [form, setForm] = useState({ username: "", email: "", password: "", otp: "" });
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState("");
  const [success, setSuccess] = useState("");
  
  // OTP related state
  const [generatedOTP, setGeneratedOTP] = useState("");
  const [otpSent, setOtpSent] = useState(false);
  const [otpVerified, setOtpVerified] = useState(false);
  const [countdown, setCountdown] = useState(0);
  const [otpExpiry, setOtpExpiry] = useState(null);
  const [lastOtpSent, setLastOtpSent] = useState(0);

  // Redirect if already authenticated
  useEffect(() => {
    if (isAuthenticated) {
      navigate("/");
    }
  }, [isAuthenticated, navigate]);

  // Countdown timer for resend OTP
  useEffect(() => {
    if (countdown > 0) {
      const timer = setTimeout(() => setCountdown(countdown - 1), 1000);
      return () => clearTimeout(timer);
    }
  }, [countdown]);

  const generateOTP = () => {
    return Math.floor(100000 + Math.random() * 900000).toString();
  };

  const sendOTP = async (email) => {
    const otp = generateOTP();
    setGeneratedOTP(otp);
    
    try {
      // Using EmailJS to send OTP directly from frontend
      const emailParams = {
        to_email: email,
        otp_code: otp,
        to_name: form.username || 'User',
        from_name: 'MFLIX',
        email: email  // For Reply To field
      };

      // Replace 'YOUR_SERVICE_ID', 'YOUR_TEMPLATE_ID', 'YOUR_PUBLIC_KEY' with your EmailJS credentials
      const response = await window.emailjs.send(
        'YOUR_SERVICE_ID',
        'YOUR_TEMPLATE_ID', 
        emailParams,
        'OWGcnCU0RFMayzRxg'
      );

      if (response.status === 200) {
        setOtpSent(true);
        setCountdown(60); // 60 second countdown
        setOtpExpiry(Date.now() + 10 * 60 * 1000); // 10 minutes expiry
        setSuccess("OTP sent to your email successfully!");
        setError("");
      } else {
        throw new Error('Failed to send OTP');
      }
    } catch (err) {
      console.error("Error sending OTP:", err);
      setError("Failed to send OTP. Please try again.");
    }
  };

  const verifyOTP = () => {
    if (Date.now() > otpExpiry) {
      setError("OTP has expired. Please request a new one.");
      setOtpSent(false);
      setGeneratedOTP("");
      return false;
    }
    
    if (form.otp === generatedOTP) {
      setOtpVerified(true);
      setSuccess("OTP verified successfully!");
      setError("");
      return true;
    } else {
      setError("Invalid OTP. Please try again.");
      return false;
    }
  };

  const handleChange = (e) => {
    setForm({ ...form, [e.target.name]: e.target.value });
    
    if (error) setError("");
    if (success) setSuccess("");
  };

  const handleSendOTP = async () => {
    if (!form.email) {
      setError("Please enter your email first");
      return;
    }

    // Basic email validation
    const emailRegex = /^[^\s@]+@[^\s@]+\.[^\s@]+$/;
    if (!emailRegex.test(form.email)) {
      setError("Please enter a valid email address");
      return;
    }

    // Rate limiting - prevent spam
    const now = Date.now();
    if (now - lastOtpSent < 60000) { // 60 seconds cooldown
      setError("Please wait before requesting another OTP");
      return;
    }

    setLoading(true);
    setLastOtpSent(now);
    await sendOTP(form.email);
    setLoading(false);
  };

  const handleResendOTP = async () => {
    if (countdown > 0) return;
    
    const now = Date.now();
    if (now - lastOtpSent < 30000) {
      setError("Please wait before requesting another OTP");
      return;
    }
    
    setLoading(true);
    setLastOtpSent(now);
    await sendOTP(form.email);
    setLoading(false);
  };

  const handleSubmit = async () => {
    // Sign In logic
    if (tab === 0) {
      if (!form.username || !form.password) {
        setError("Please fill in all required fields");
        return;
      }

      setLoading(true);
      setError("");
      
      try {
        const credentials = { username: form.username, password: form.password };
        const result = await login(credentials);
        
        if (result.success) {
          navigate("/");
        } else {
          setError(result.error);
        }
      } catch (err) {
        console.error("Authentication error:", err);
        setError("An unexpected error occurred");
      } finally {
        setLoading(false);
      }
    }
    
    // Sign Up logic
    else {
      if (!form.username || !form.email || !form.password) {
        setError("Please fill in all required fields");
        return;
      }

      if (!otpSent) {
        setError("Please send and verify OTP first");
        return;
      }

      if (!otpVerified) {
        if (!form.otp) {
          setError("Please enter the OTP");
          return;
        }
        
        if (!verifyOTP()) {
          return;
        }
      }

      setLoading(true);
      setError("");
      
      try {
        const credentials = { 
          username: form.username, 
          email: form.email, 
          password: form.password 
        };

        const result = await login(credentials);
        
        if (result.success) {
          navigate("/");
        } else {
          setError(result.error);
        }
      } catch (err) {
        console.error("Authentication error:", err);
        setError("An unexpected error occurred");
      } finally {
        setLoading(false);
      }
    }
  };

  const handleTabChange = (_, newVal) => {
    setTab(newVal);
    setError("");
    setSuccess("");
    // Reset OTP state when switching tabs
    setOtpSent(false);
    setOtpVerified(false);
    setGeneratedOTP("");
    setOtpExpiry(null);
    setCountdown(0);
    setLastOtpSent(0);
    setForm({ username: "", email: "", password: "", otp: "" });
  };

  return (
    <Wrapper>
      <StyledTabs 
        value={tab} 
        onChange={handleTabChange} 
        textColor="inherit" 
        indicatorColor="primary"
      >
        <Tab label="Sign In" />
        <Tab label="Sign Up" />
      </StyledTabs>

      {error && (
        <Alert severity="error" sx={{ mb: 2, backgroundColor: '#d32f2f', color: 'white' }}>
          {error}
        </Alert>
      )}

      {success && (
        <Alert severity="success" sx={{ mb: 2, backgroundColor: '#2e7d32', color: 'white' }}>
          {success}
        </Alert>
      )}

      {tab === 1 && (
        <>
          <StyledInput 
            label="Email" 
            variant="outlined" 
            fullWidth 
            name="email" 
            type="email"
            value={form.email}
            onChange={handleChange} 
          />
          
          <OTPContainer>
            <StyledInput 
              label="Enter OTP" 
              variant="outlined" 
              fullWidth 
              name="otp" 
              value={form.otp}
              onChange={handleChange}
              disabled={!otpSent}
              sx={{ marginBottom: 0 }}
            />
            <Button 
              variant="outlined" 
              onClick={handleSendOTP}
              disabled={loading || !form.email || (otpSent && !otpVerified)}
              sx={{ 
                minWidth: '80px',
                color: 'white',
                borderColor: 'gray',
                '&:hover': { borderColor: 'white' }
              }}
            >
              {otpSent && !otpVerified ? 'Sent' : 'Send OTP'}
            </Button>
          </OTPContainer>

          {otpSent && !otpVerified && (
            <Box sx={{ textAlign: 'center', marginBottom: 2 }}>
              <Typography variant="body2" color="gray">
                Didn't receive OTP?{' '}
                <ResendButton 
                  onClick={handleResendOTP}
                  disabled={countdown > 0}
                  sx={{ 
                    color: countdown > 0 ? 'gray' : 'white',
                    textDecoration: 'underline'
                  }}
                >
                  {countdown > 0 ? `Resend in ${countdown}s` : 'Resend OTP'}
                </ResendButton>
              </Typography>
            </Box>
          )}
        </>
      )}
      
      <StyledInput 
        label="Username" 
        variant="outlined" 
        fullWidth 
        name="username" 
        value={form.username}
        onChange={handleChange} 
      />
      
      <StyledInput 
        label="Password" 
        type="password" 
        variant="outlined" 
        fullWidth 
        name="password" 
        value={form.password}
        onChange={handleChange} 
      />

      <Button 
        variant="contained" 
        color="primary" 
        fullWidth 
        onClick={handleSubmit}
        disabled={loading || (tab === 1 && otpSent && !otpVerified)}
      >
        {loading ? "Loading..." : (tab === 0 ? "Sign In" : "Sign Up")}
      </Button>
    </Wrapper>
  );
};

export default SignInSignUp;
