// src/pages/MovieDetails.jsx
import React, { Component } from "react";
import axios from "axios";
import { useParams } from "react-router-dom";

// Wrapper to access hooks inside class
function withRouter(Component) {
  return (props) => {
    const params = useParams();
    return <Component {...props} params={params} />;
  };
}

class MovieDetails extends Component {
  state = {
    movie: null,
    loading: true,
    error: null,
  };

  componentDidMount() {
    const { id } = this.props.params;
    const apiUrl = import.meta.env.VITE_API_URL || "http://192.168.:3008";

    axios
      .get(`${apiUrl}/api/movies/${id}`)
      .then((res) => {
        this.setState({ movie: res.data, loading: false });
      })
      .catch((err) => {
        console.error("Error fetching movie details:", err);
        this.setState({ error: "Movie not found", loading: false });
      });
  }

  render() {
    const { movie, loading, error } = this.state;

    if (loading) return <div className="container mt-5">Loading...</div>;
    if (error) return <div className="container mt-5 text-danger">{error}</div>;

    return (
      <div className="container mt-5">
        <div className="row">
          <div className="col-md-4">
            <img
              src={`/img/${movie.posterimage}`}
              alt={movie.title}
              className="img-fluid rounded shadow"
            />
          </div>
          <div className="col-md-8">
            <h2>{movie.title}</h2>
            <p><strong>Language:</strong> {movie.language}</p>
            <p><strong>Duration:</strong> {movie.duration} min</p>
            <p><strong>Release Date:</strong> {new Date(movie.releasedate).toDateString()}</p>
            <p><strong>Rating:</strong> {movie.averagerating}/10</p>
            <hr />
            <p>{movie.synopsis}</p>
          </div>
        </div>
      </div>
    );
  }
}

export default withRouter(MovieDetails);
