import { Box, ListItem, List, Typography, styled } from "@mui/material";
import { Star } from '@mui/icons-material';
import { Link } from "react-router-dom";

const Banner = styled('img')({
  width: 83,
  height: 120,
  objectFit: 'cover',
  borderRadius: 5
});

const Container = styled(List)`
  display: flex;
  align-items: center;
  margin-bottom: 16px;
  background-color: #1c1c1c;
  padding: 12px;
  border-radius: 8px;
`;

const MovieTitle = styled(Typography)`
  font-weight: bold;
  color: #fff;
`;

const MoviesList = ({ movies = [] }) => {
  return (
    <>
      {movies.map((movie) => (
        <Container key={movie.movieid}>
          <ListItem>
            <Link to={`/movie/${movie.movieid}`}>
              <Banner src={`/img/${movie.posterimage}`} alt={movie.title} />
            </Link>
          </ListItem>

          <ListItem>
            <Link to={`/movie/${movie.movieid}`} style={{ textDecoration: "none" }}>
              <MovieTitle>{movie.title}</MovieTitle>
            </Link>
          </ListItem>

          <ListItem>
            <Star color="warning" />
            <Typography sx={{ color: "#fff", ml: 1 }}>{movie.averagerating}</Typography>
          </ListItem>

          <ListItem>
            <Typography sx={{ color: "#ccc" }}>
              {new Date(movie.releasedate).toLocaleDateString()}
            </Typography>
          </ListItem>
        </Container>
      ))}
    </>
  );
};

export default MoviesList;
