// MoviesList.jsx - Improved version
import { Box, ListItem, List, Typography, styled } from "@mui/material";
import { Star } from '@mui/icons-material';
import { useNavigate } from "react-router-dom";

const Banner = styled('img')({
  width: 83,
  height: 120,
  objectFit: 'cover',
  borderRadius: 8,
  marginRight: 10,
});

const Container = styled(Box)` // Changed from List to Box
  display: flex;
  align-items: center;
  background-color: #1c1c1c;
  color: white;
  border-radius: 10px;
  padding: 10px;
  margin: 10px 0;
  transition: transform 0.2s ease-in-out;
  cursor: pointer;
  &:hover {
    transform: scale(1.02);
    background-color: #2a2a2a;
  }
`;

const MoviesList = ({ movies }) => {
  const navigate = useNavigate();
  
  // Debug log
  console.log("MoviesList received movies:", movies);
  
  // Handle empty or undefined movies
  if (!movies || movies.length === 0) {
    return (
      <Typography sx={{ color: '#fff', mt: 2 }}>
        No movies to display
      </Typography>
    );
  }

  return (
    <Box>
      {movies.map((movie) => (
        <Container key={movie.movieid} onClick={() => navigate(`/movie/${movie.movieid}`)}>
          <Box sx={{ display: 'flex', alignItems: 'center', width: '100%' }}>
            <Banner 
              src={`/img/${movie.titleimage}`} 
              alt={movie.title}
              onError={(e) => {
                console.log(`Image failed to load: /img/${movie.titleimage}`);
                e.target.src = '/img/placeholder.png'; // Fallback image
              }}
            />
            <Box sx={{ flex: 1, display: 'flex', justifyContent: 'space-between', alignItems: 'center' }}>
              <Typography variant="h6" sx={{ flex: 1 }}>
                {movie.title}
              </Typography>
              <Box sx={{ display: 'flex', alignItems: 'center', mx: 2 }}>
                <Star color="warning" sx={{ mr: 0.5 }} />
                <Typography>
                  {movie.averagerating ? movie.averagerating.toFixed(1) : 'N/A'}
                </Typography>
              </Box>
              <Typography>
                {movie.releasedate ? new Date(movie.releasedate).getFullYear() : 'Unknown'}
              </Typography>
            </Box>
          </Box>
        </Container>
      ))}
    </Box>
  );
};

export default MoviesList;
