// CastSection.jsx
import React from 'react';
import { Box, Typography, styled } from '@mui/material';

const CastContainer = styled(Box)`
  margin: 40px 0;
`;

const CastGrid = styled(Box)`
  display: grid;
  grid-template-columns: repeat(auto-fill, minmax(150px, 1fr));
  gap: 20px;
  margin-top: 20px;
`;

const CastCard = styled(Box)`
  display: flex;
  flex-direction: column;
  align-items: center;
  cursor: pointer;
  transition: transform 0.2s;
  padding: 10px;
  border-radius: 8px;
  
  &:hover {
    transform: scale(1.05);
    background-color: rgba(255, 255, 255, 0.1);
  }
`;

const CastImage = styled('img')`
  width: 120px;
  height: 120px;
  border-radius: 50%;
  object-fit: cover;
  border: 3px solid #f5c518;
  margin-bottom: 10px;
`;

const CastName = styled(Typography)`
  color: #ffffff;
  font-size: 14px;
  font-weight: 600;
  text-align: center;
  margin-bottom: 5px;
`;

const CastRole = styled(Typography)`
  color: #cccccc;
  font-size: 12px;
  text-align: center;
  font-style: italic;
`;

const SectionTitle = styled(Typography)`
  color: #ffffff;
  font-size: 24px;
  font-weight: 600;
  margin-bottom: 20px;
`;

const CastSection = ({ cast, formatDate, isAuthenticated, movieId, onActorClick }) => {
  if (!cast || cast.length === 0) {
    return null;
  }

  // Debug: Log the cast data structure
  console.log('Cast data:', cast);
  console.log('First cast member:', cast[0]);

  const handleActorClick = (actor) => {
    console.log('Actor clicked:', actor);
    
    // Try different possible ID field names
    const actorId = actor.id || actor.actor_id || actor.actorid || actor.actorId;
    
    console.log('Actor ID found:', actorId);
    
    if (actorId && onActorClick) {
      onActorClick(actorId);
    } else {
      console.warn('No actor ID found or onActorClick not provided');
    }
  };

  return (
    <CastContainer>
      <SectionTitle>Cast</SectionTitle>
      <CastGrid>
        {cast.map((actor, index) => (
          <CastCard 
            key={actor.id || actor.actor_id || actor.actorid || actor.actorId || index}
            onClick={() => handleActorClick(actor)}
          >
            <CastImage 
              src={`/img/${actor.image}`} 
              alt={actor.name}
              onError={(e) => {
                e.target.src = '/img/default-actor.jpg';
              }}
            />
            <CastName>{actor.name}</CastName>
            {actor.role && <CastRole>as {actor.role}</CastRole>}
          </CastCard>
        ))}
      </CastGrid>
    </CastContainer>
  );
};

export default CastSection;
