// MoviesList.jsx
import { Box, ListItem, List, Typography, styled } from "@mui/material";
import { Star } from '@mui/icons-material';
import { useNavigate } from "react-router-dom";

const Banner = styled('img')({
  width: 83,
  height: 120,
  objectFit: 'cover',
  borderRadius: 8,
  marginRight: 10,
});

const Container = styled(List)`
  display: flex;
  align-items: center;
  background-color: #1c1c1c;
  color: white;
  border-radius: 10px;
  padding: 10px;
  margin: 10px 0;
  transition: transform 0.2s ease-in-out;
  cursor: pointer;
  &:hover {
    transform: scale(1.02);
    background-color: #2a2a2a;
  }
`;

const MoviesList = ({ movies }) => {
  const navigate = useNavigate();
  return (
    <>
      {movies.map((movie) => (
        <Container key={movie.movieid} onClick={() => navigate(`/movie/${movie.movieid}`)}>
          <ListItem>
            <Banner src={`/img/${movie.titleimage}`} alt="poster" />
          </ListItem>
          <ListItem>
            <Typography variant="h6">{movie.title}</Typography>
          </ListItem>
          <ListItem>
            <Star color="warning" />
            <Typography>{movie.averagerating}</Typography>
          </ListItem>
          <ListItem>
            <Typography>{new Date(movie.releasedate).getFullYear()}</Typography>
          </ListItem>
        </Container>
      ))}
    </>
  );
};

export default MoviesList;
