// SignInSignUp.jsx
import React, { useState, useEffect } from "react";
import { useNavigate } from "react-router-dom";
import { Box, Typography, TextField, Button, styled, Paper, Tabs, Tab, Alert } from "@mui/material";
import { useAuth } from "../components/contexts/AuthContext";

const Wrapper = styled(Paper)`
  max-width: 400px;
  margin: 80px auto;
  padding: 40px;
  background-color: #1c1c1c;
  color: white;
`;

const StyledInput = styled(TextField)`
  margin-bottom: 20px;
  & .MuiInputBase-input {
    color: #d3d3d3;
    background-color: #1c1c1c;
  }
  & .MuiInputLabel-root {
    color: gray;
  }
  & .MuiOutlinedInput-root .MuiOutlinedInput-notchedOutline {
    border-color: gray;
  }
  & .MuiOutlinedInput-root.Mui-focused .MuiOutlinedInput-notchedOutline {
    border-color: gray;
  }
  & .MuiOutlinedInput-root:hover .MuiOutlinedInput-notchedOutline {
    border-color: gray;
  }
  & .MuiOutlinedInput-root.Mui-focused {
    background-color: #1c1c1c;
  }
  & .MuiOutlinedInput-root.Mui-filled {
    background-color: #1c1c1c;
  }
`;

const StyledTabs = styled(Tabs)`
  margin-bottom: 20px;
`;

const SignInSignUp = () => {
  const navigate = useNavigate();
  const { login, isAuthenticated } = useAuth(); 
  const [tab, setTab] = useState(0);
  const [form, setForm] = useState({ username: "", email: "", password: "" });
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState("");

  // Redirect if already authenticated
  useEffect(() => {
    if (isAuthenticated) {
      navigate("/");
    }
  }, [isAuthenticated, navigate]);

  const handleChange = (e) => {
    setForm({ ...form, [e.target.name]: e.target.value });
    
    if (error) setError("");
  };

  const handleSubmit = async () => {
    // Basic validation
    if (!form.username || !form.password) {
      setError("Please fill in all required fields");
      return;
    }

    if (tab === 1 && !form.email) {
      setError("Please enter your email");
      return;
    }

    setLoading(true);
    setError("");
    
    try {
      const credentials = tab === 0 
        ? { username: form.username, password: form.password }
        : { username: form.username, email: form.email, password: form.password };

      const result = await login(credentials);
      
      if (result.success) {
        navigate("/");
      } else {
        setError(result.error);
      }
    } catch (err) {
      console.error("Authentication error:", err);
      setError("An unexpected error occurred");
    } finally {
      setLoading(false);
    }
  };

  const handleTabChange = (_, newVal) => {
    setTab(newVal);
    setError(""); // Clear error when switching tabs
  };

  return (
    <Wrapper>
      <StyledTabs 
        value={tab} 
        onChange={handleTabChange} 
        textColor="inherit" 
        indicatorColor="primary"
      >
        <Tab label="Sign In" />
        <Tab label="Sign Up" />
      </StyledTabs>

      {error && (
        <Alert severity="error" sx={{ mb: 2, backgroundColor: '#d32f2f', color: 'white' }}>
          {error}
        </Alert>
      )}

      {tab === 1 && (
        <StyledInput 
          label="Email" 
          variant="outlined" 
          fullWidth 
          name="email" 
          type="email"
          value={form.email}
          onChange={handleChange} 
        />
      )}
      
      <StyledInput 
        label="Username" 
        variant="outlined" 
        fullWidth 
        name="username" 
        value={form.username}
        onChange={handleChange} 
      />
      
      <StyledInput 
        label="Password" 
        type="password" 
        variant="outlined" 
        fullWidth 
        name="password" 
        value={form.password}
        onChange={handleChange} 
      />

      <Button 
        variant="contained" 
        color="primary" 
        fullWidth 
        onClick={handleSubmit}
        disabled={loading}
      >
        {loading ? "Loading..." : (tab === 0 ? "Sign In" : "Sign Up")}
      </Button>
    </Wrapper>
  );
};

export default SignInSignUp;
