// CastSection.jsx
import React from "react";
import {
  Box,
  Typography,
  Grid,
  Card,
  CardContent,
  CardMedia,
  styled,
} from "@mui/material";
import { Star } from "@mui/icons-material";

const CastCard = styled(Card)`
  background: rgba(40, 40, 40, 0.9);
  color: white;
  height: 100%;
  transition: transform 0.3s ease;
  &:hover {
    transform: translateY(-5px);
  }
`;

function CastSection({ cast, formatDate }) {
  if (!cast || cast.length === 0) {
    return null;
  }

  return (
    <Box mb={5}>
      <Typography variant="h4" gutterBottom sx={{ color: 'white', mb: 3 }}>
        Cast
      </Typography>
      <Grid container spacing={3}>
        {cast.map((actor, index) => (
          <Grid item xs={12} sm={6} md={4} lg={3} key={index}>
            <CastCard>
              <CardMedia
                component="img"
                height="300"
                image={`/img/${actor.Image}`}
                alt={actor.Name}
                sx={{ objectFit: 'cover' }}
              />
              <CardContent>
                <Typography variant="h6" gutterBottom>
                  {actor.Name}
                </Typography>
                <Typography variant="body2" color="grey.400" gutterBottom>
                  as {actor.RoleName}
                </Typography>
                <Box display="flex" alignItems="center" gap={1}>
                  <Star sx={{ color: '#f5c518', fontSize: 16 }} />
                  <Typography variant="body2">
                    {actor.CastRating}/10
                  </Typography>
                </Box>
                <Typography variant="body2" color="grey.500" mt={1}>
                  {actor.Nationality} â€¢ Born {formatDate(actor.DateOfBirth)}
                </Typography>
              </CardContent>
            </CastCard>
          </Grid>
        ))}
      </Grid>
    </Box>
  );
}

export default CastSection;
