import { useEffect, useState } from "react";
import axios from "axios";
import MoviesList from "../components/common/MoviesList";
import { Typography, Container } from "@mui/material";
import url from "../constants/url";

const Watchlist = () => {
  const [movies, setMovies] = useState([]);
  const [watchlistName, setWatchlistName] = useState("Your Watchlist");

  useEffect(() => {
    const fetchWatchlist = async () => {
      try {
        // The axios interceptor will automatically add the Authorization header
        const response = await axios.get(`${url}/api/users/watchlist`, {
          withCredentials: true, // Still keep this for cookie backup
        });

        const watchlistData = response.data;
        setMovies(watchlistData.movies || []);
        setWatchlistName(watchlistData.name || "Your Watchlist");
      } catch (error) {
        console.error("Failed to fetch watchlist:", error);
        
        // Check if it's an authentication error
        if (error.response?.status === 401) {
          console.log("User not authenticated, clearing local storage");
          localStorage.removeItem('user');
          localStorage.removeItem('token');
          // Optionally redirect to login
          // window.location.href = '/login';
        }
        
        setMovies([]);
      }
    };

    fetchWatchlist();
  }, []);

  return (
    <Container sx={{ mt: 4 }}>
      <Typography variant="h5" gutterBottom sx={{ color: '#fff' }}>
        {watchlistName}
      </Typography>
      <MoviesList movies={movies} />
    </Container>
  );
};

export default Watchlist;
