import React, { useState } from "react";
import { useNavigate } from "react-router-dom";
import { Box, Typography, TextField, Button, styled, Paper, Tabs, Tab } from "@mui/material";
import axios from "axios";
import url from "../constants/url";

const Wrapper = styled(Paper)`
  max-width: 400px;
  margin: 80px auto;
  padding: 40px;
  background-color: #1c1c1c;
  color: white;
`;

const StyledInput = styled(TextField)`
  margin-bottom: 20px;
  & .MuiInputBase-input {
    color: #d3d3d3;
    background-color: #1c1c1c;
  }
  & .MuiInputLabel-root {
    color: gray;
  }
  & .MuiOutlinedInput-root .MuiOutlinedInput-notchedOutline {
    border-color: gray;
  }
  & .MuiOutlinedInput-root.Mui-focused .MuiOutlinedInput-notchedOutline {
    border-color: gray;
  }
  & .MuiOutlinedInput-root:hover .MuiOutlinedInput-notchedOutline {
    border-color: gray;
  }
  & .MuiOutlinedInput-root.Mui-focused {
    background-color: #1c1c1c;
  }
  & .MuiOutlinedInput-root.Mui-filled {
    background-color: #1c1c1c;
  }
`;

const StyledTabs = styled(Tabs)`
  margin-bottom: 20px;
`;

const SignInSignUp = ({ setUser }) => {
  const navigate = useNavigate();
  const [tab, setTab] = useState(0);
  const [form, setForm] = useState({ username: "", email: "", password: "" });
  const [loading, setLoading] = useState(false);

  const handleChange = (e) => {
    setForm({ ...form, [e.target.name]: e.target.value });
  };

  const handleSubmit = async () => {
    // Basic validation
    if (!form.username || !form.password) {
      alert("Please fill in all required fields");
      return;
    }

    if (tab === 1 && !form.email) {
      alert("Please enter your email");
      return;
    }

    setLoading(true);
    
    try {
      const backendUrl = url;
      const endpoint = tab === 0 ? "/api/auth/signin" : "/api/auth/signup";
      const { data } = await axios.post(`${backendUrl}${endpoint}`, form, {
        withCredentials: true, 
      });

      // Update localStorage
      localStorage.setItem("user", JSON.stringify(data.user));
      
      // Update state in parent component
      setUser(data.user);
      
      // Dispatch custom event to notify other components
      window.dispatchEvent(new Event("userChanged"));

      navigate("/");
    } catch (err) {
      console.error("Authentication error:", err);
      alert(err.response?.data?.message || "Authentication failed");
    } finally {
      setLoading(false);
    }
  };

  return (
    <Wrapper>
      <StyledTabs 
        value={tab} 
        onChange={(_, newVal) => setTab(newVal)} 
        textColor="inherit" 
        indicatorColor="primary"
      >
        <Tab label="Sign In" />
        <Tab label="Sign Up" />
      </StyledTabs>

      {tab === 1 && (
        <StyledInput 
          label="Email" 
          variant="outlined" 
          fullWidth 
          name="email" 
          type="email"
          value={form.email}
          onChange={handleChange} 
        />
      )}
      
      <StyledInput 
        label="Username" 
        variant="outlined" 
        fullWidth 
        name="username" 
        value={form.username}
        onChange={handleChange} 
      />
      
      <StyledInput 
        label="Password" 
        type="password" 
        variant="outlined" 
        fullWidth 
        name="password" 
        value={form.password}
        onChange={handleChange} 
      />

      <Button 
        variant="contained" 
        color="primary" 
        fullWidth 
        onClick={handleSubmit}
        disabled={loading}
      >
        {loading ? "Loading..." : (tab === 0 ? "Sign In" : "Sign Up")}
      </Button>
    </Wrapper>
  );
};

export default SignInSignUp;
