// ReviewsSection.jsx
import React, { useState } from "react";
import {
  Box,
  Typography,
  Chip,
  Divider,
  IconButton,
  Paper,
  styled,
  Button,
  TextField,
  FormControlLabel,
  Checkbox,
  Alert,
} from "@mui/material";
import { ThumbUp, ThumbDown, Add, Visibility, VisibilityOff } from "@mui/icons-material";

const ReviewCard = styled(Paper)`
  background: rgba(50, 50, 50, 0.9);
  color: white;
  padding: 20px;
  margin-bottom: 15px;
  border-radius: 12px;
`;

const SubmitButton = styled(Button)`
  background: #1976d2;
  color: white;
  &:hover {
    background: #1565c0;
  }
`;

const SpoilerButton = styled(Button)`
  background: ${props => props.showspoiler ? 'rgba(255, 152, 0, 0.8)' : 'rgba(255, 152, 0, 0.2)'};
  color: white;
  border: 1px solid #ff9800;
  &:hover {
    background: rgba(255, 152, 0, 0.9);
  }
`;

function ReviewsSection({ reviews, formatDate, onSubmitReview, onVoteReview, userHasReviewed = false }) {
  const [showReviewForm, setShowReviewForm] = useState(false);
  const [reviewForm, setReviewForm] = useState({
    title: '',
    content: '',
    rating: 5,
    hasSpoiler: false
  });
  const [spoilerVisibility, setSpoilerVisibility] = useState({});

  const handleSubmitReview = async () => {
    if (!reviewForm.title.trim() || !reviewForm.content.trim()) {
      alert('Please fill in all fields');
      return;
    }

    try {
      await onSubmitReview(reviewForm);
      setReviewForm({ title: '', content: '', rating: 5, hasSpoiler: false });
      setShowReviewForm(false);
    } catch (error) {
      console.error('Error submitting review:', error);
    }
  };

  const toggleSpoilerVisibility = (reviewIndex) => {
    setSpoilerVisibility(prev => ({
      ...prev,
      [reviewIndex]: !prev[reviewIndex]
    }));
  };

  const handleVote = async (reviewId, voteType) => {
    try {
      await onVoteReview(reviewId, voteType);
    } catch (error) {
      console.error('Error voting:', error);
    }
  };

  if (!reviews || reviews.length === 0) {
    return (
      <Box>
        <Typography variant="h5" gutterBottom sx={{ color: 'white' }}>
          Reviews (0)
        </Typography>
        <Button
          variant="contained"
          startIcon={<Add />}
          onClick={() => setShowReviewForm(true)}
          disabled={userHasReviewed}
          sx={{ 
            mb: 2, 
            background: userHasReviewed ? 'grey.600' : '#1976d2', 
            color: 'white',
            '&:disabled': {
              background: 'grey.600',
              color: 'grey.400'
            }
          }}
        >
          {userHasReviewed ? 'Already Reviewed' : 'Write a Review'}
        </Button>
        {showReviewForm && !userHasReviewed && (
          <ReviewCard>
            <Typography variant="h6" gutterBottom>
              Write Your Review
            </Typography>
            <TextField
              fullWidth
              label="Review Title"
              value={reviewForm.title}
              onChange={(e) => setReviewForm(prev => ({ ...prev, title: e.target.value }))}
              sx={{ mb: 2, '& .MuiInputLabel-root': { color: 'white' }, '& .MuiOutlinedInput-root': { color: 'white' } }}
            />
            <Box mb={2}>
              <Typography component="legend" sx={{ color: 'white', mb: 1 }}>Rating (1-10)</Typography>
              <Box display="flex" alignItems="center" gap={2}>
                <TextField
                  type="number"
                  inputProps={{ min: 1, max: 10 }}
                  value={reviewForm.rating}
                  onChange={(e) => {
                    const value = Math.max(1, Math.min(10, parseInt(e.target.value) || 1));
                    setReviewForm(prev => ({ ...prev, rating: value }));
                  }}
                  sx={{ 
                    width: '80px',
                    '& .MuiInputLabel-root': { color: 'white' }, 
                    '& .MuiOutlinedInput-root': { color: 'white' },
                    '& .MuiOutlinedInput-input': { textAlign: 'center' }
                  }}
                />
                <Typography variant="body2" sx={{ color: 'grey.400' }}>
                  / 10
                </Typography>
              </Box>
            </Box>
            <TextField
              fullWidth
              multiline
              rows={4}
              label="Review Content"
              value={reviewForm.content}
              onChange={(e) => setReviewForm(prev => ({ ...prev, content: e.target.value }))}
              sx={{ mb: 2, '& .MuiInputLabel-root': { color: 'white' }, '& .MuiOutlinedInput-root': { color: 'white' } }}
            />
            <FormControlLabel
              control={
                <Checkbox
                  checked={reviewForm.hasSpoiler}
                  onChange={(e) => setReviewForm(prev => ({ ...prev, hasSpoiler: e.target.checked }))}
                  sx={{ color: 'white' }}
                />
              }
              label="This review contains spoilers"
              sx={{ color: 'white', mb: 2 }}
            />
            <Box display="flex" gap={2}>
              <SubmitButton onClick={handleSubmitReview}>
                Submit Review
              </SubmitButton>
              <Button
                variant="outlined"
                onClick={() => setShowReviewForm(false)}
                sx={{ color: 'white', borderColor: 'white' }}
              >
                Cancel
              </Button>
            </Box>
          </ReviewCard>
        )}
      </Box>
    );
  }

  return (
    <Box>
      <Box display="flex" justifyContent="space-between" alignItems="center" mb={2}>
        <Typography variant="h5" sx={{ color: 'white' }}>
          Reviews ({reviews.length})
        </Typography>
        <Button
          variant="contained"
          startIcon={<Add />}
          onClick={() => setShowReviewForm(true)}
          disabled={userHasReviewed}
          sx={{ 
            background: userHasReviewed ? 'grey.600' : '#1976d2', 
            color: 'white',
            '&:disabled': {
              background: 'grey.600',
              color: 'grey.400'
            }
          }}
        >
          {userHasReviewed ? 'Already Reviewed' : 'Write a Review'}
        </Button>
      </Box>

              {showReviewForm && !userHasReviewed && (
        <ReviewCard sx={{ mb: 3 }}>
          <Typography variant="h6" gutterBottom>
            Write Your Review
          </Typography>
          <TextField
            fullWidth
            label="Review Title"
            value={reviewForm.title}
            onChange={(e) => setReviewForm(prev => ({ ...prev, title: e.target.value }))}
            sx={{ mb: 2, '& .MuiInputLabel-root': { color: 'white' }, '& .MuiOutlinedInput-root': { color: 'white' } }}
          />
          <Box mb={2}>
            <Typography component="legend" sx={{ color: 'white', mb: 1 }}>Rating (1-10)</Typography>
            <Box display="flex" alignItems="center" gap={2}>
              <TextField
                type="number"
                inputProps={{ min: 1, max: 10 }}
                value={reviewForm.rating}
                onChange={(e) => {
                  const value = Math.max(1, Math.min(10, parseInt(e.target.value) || 1));
                  setReviewForm(prev => ({ ...prev, rating: value }));
                }}
                sx={{ 
                  width: '80px',
                  '& .MuiInputLabel-root': { color: 'white' }, 
                  '& .MuiOutlinedInput-root': { color: 'white' },
                  '& .MuiOutlinedInput-input': { textAlign: 'center' }
                }}
              />
              <Typography variant="body2" sx={{ color: 'grey.400' }}>
                / 10
              </Typography>
            </Box>
          </Box>
          <TextField
            fullWidth
            multiline
            rows={4}
            label="Review Content"
            value={reviewForm.content}
            onChange={(e) => setReviewForm(prev => ({ ...prev, content: e.target.value }))}
            sx={{ mb: 2, '& .MuiInputLabel-root': { color: 'white' }, '& .MuiOutlinedInput-root': { color: 'white' } }}
          />
          <FormControlLabel
            control={
              <Checkbox
                checked={reviewForm.hasSpoiler}
                onChange={(e) => setReviewForm(prev => ({ ...prev, hasSpoiler: e.target.checked }))}
                sx={{ color: 'white' }}
              />
            }
            label="This review contains spoilers"
            sx={{ color: 'white', mb: 2 }}
          />
          <Box display="flex" gap={2}>
            <SubmitButton onClick={handleSubmitReview}>
              Submit Review
            </SubmitButton>
            <Button
              variant="outlined"
              onClick={() => setShowReviewForm(false)}
              sx={{ color: 'white', borderColor: 'white' }}
            >
              Cancel
            </Button>
          </Box>
        </ReviewCard>
      )}

      {reviews.map((review, index) => (
        <ReviewCard key={index}>
          <Box display="flex" justifyContent="space-between" alignItems="flex-start" mb={2}>
            <Box>
              <Typography variant="h6" gutterBottom>
                {review.title}
              </Typography>
              <Box display="flex" alignItems="center" gap={1}>
                <Typography variant="body2" sx={{ 
                  background: 'linear-gradient(45deg, #ff6b6b, #ffd93d)', 
                  WebkitBackgroundClip: 'text',
                  WebkitTextFillColor: 'transparent',
                  fontWeight: 'bold',
                  fontSize: '1.1rem'
                }}>
                  {review.ratingscore}/10
                </Typography>
              </Box>
            </Box>
            <Box display="flex" gap={1}>
              {review.hasspoiler && (
                <>
                  <SpoilerButton
                    size="small"
                    startIcon={spoilerVisibility[index] ? <VisibilityOff /> : <Visibility />}
                    onClick={() => toggleSpoilerVisibility(index)}
                    showspoiler={spoilerVisibility[index]}
                  >
                    {spoilerVisibility[index] ? 'Hide Spoiler' : 'Show Spoiler'}
                  </SpoilerButton>
                  <Chip
                    label="Spoiler"
                    color="warning"
                    size="small"
                  />
                </>
              )}
            </Box>
          </Box>
          
          {review.hasspoiler && !spoilerVisibility[index] ? (
            <Alert severity="warning" sx={{ mb: 2 }}>
              This review contains spoilers. Click "Show Spoiler" to view the content.
            </Alert>
          ) : (
            <Typography variant="body1" mb={2} sx={{ lineHeight: 1.6 }}>
              {review.content}
            </Typography>
          )}
          
          <Divider sx={{ backgroundColor: 'grey.700', mb: 2 }} />
          
          <Box display="flex" alignItems="center" gap={3}>
            <Box display="flex" alignItems="center" gap={1}>
              <IconButton 
                size="small" 
                sx={{ color: 'grey.400' }}
                onClick={() => handleVote(review.id, 'upvote')}
              >
                <ThumbUp fontSize="small" />
              </IconButton>
              <Typography variant="body2">{review.upvotes}</Typography>
            </Box>
            <Box display="flex" alignItems="center" gap={1}>
              <IconButton 
                size="small" 
                sx={{ color: 'grey.400' }}
                onClick={() => handleVote(review.id, 'downvote')}
              >
                <ThumbDown fontSize="small" />
              </IconButton>
              <Typography variant="body2">{review.downvotes}</Typography>
            </Box>
            <Typography variant="body2" color="grey.500" ml="auto">
              {formatDate(review.createdat)}
            </Typography>
          </Box>
        </ReviewCard>
      ))}
    </Box>
  );
}

export default ReviewsSection;
